import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Menus } from "../models/manu";
import BaseService from "../../../service/base.service.tsx";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal } from "react-bootstrap";
import { Category } from "./inum.component";
import { Delete, View } from "../../../defultComponent/funPublik";



const MenusIndex = (props) => {
    const { state } = useAppState();

    const [isReady, setIsReady] = useState(false);
    const [listMenus, setListMenus] = useState([], Array(Menus));
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [nameForDelete, setNameForDelete] = useState("")
    const [categoryForDelete, setCategoryForDelete] = useState("")
    const [idForDelete, setIdForDelete] = useState("")


    let pathname = window.location.pathname


    const ModalDelete = (name, category, id) => {
        setNameForDelete(name)
        setCategoryForDelete(category)
        setIdForDelete(id)
        console.log("name, category, id", name, category, id)
        handleShow()

        // Get the modal
        var modal = document.getElementById("myModal");

        // Get the button that opens the modal
        var btn = document.getElementById("myBtn");

        // Get the <span> element that closes the modal
        var span = document.getElementsByClassName("close")[0];

        // When the user clicks the button, open the modal 
        btn.onclick = function () {
            modal.style.display = "block";
        }

        // When the user clicks on <span> (x), close the modal
        span.onclick = function () {
            modal.style.display = "none";
        }

        // When the user clicks anywhere outside of the modal, close it
        window.onclick = function (event) {
            if (event.target == modal) {
                modal.style.display = "none";
            }
        }
    }

    useEffect(() => {
        BaseService.getAll(pathname).then((rp) => {
            if (rp.Status) {
                const data = rp.Data;

                console.log("isReady1", data);
                setIsReady(true);
                setListMenus(data);
                console.log("isReady2---", isReady, listMenus, state);
            } else {
                setIsReady(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);

    return (
        <>
            <div>
                <table>
                    <tr>
                        <th >Action</th>
                        <th>מחיר</th>
                        <th>קטגוריה</th>
                        <th>שם מנה</th>
                        <th>id</th>
                    </tr>
                    {isReady &&
                        listMenus.map((item, i) => {
                            return (
                                <tr key={i} >
                                    <td >
                                        {/*<div class="icons">
                                            <Delete />
                                            <View />
                                            <div class="miniNavbarButton" onClick={() => window.location.href = "/menu/" + item.category + "/" + item.id}>הצגה</div>

                                            <button id="myBtn" onClick={() => ModalDelete(item.name, item.category, item.id)}>מחיקה</button>

                                             <div id="myModal" class="modal">

                                                <div class="modal-content">
                                                    <h2>מחיקת מנה</h2>
                                                    <p>?{nameForDelete} בטוח שאתה רוצה למחוק את</p>
                                                    <div>
                                                        <button class="close"
                                                            onClick={() =>
                                                                BaseService.delete(pathname + "/", {
                                                                    id: idForDelete,
                                                                }).then((rp) => {
                                                                    console.log("rp", rp)
                                                                    if (rp.Status) {
                                                                        const listMenusUpdated = listMenus.filter(
                                                                            (menus) => menus.id !== idForDelete
                                                                        );
                                                                        setListMenus(listMenusUpdated);
                                                                        console.log("listMenusUpdated", listMenusUpdated);
                                                                    } else {
                                                                        toastr.error(rp.Messages);
                                                                        console.log("Messages: " + rp.Messages);
                                                                        console.log("Exception: " + rp.Exception);
                                                                    }
                                                                })
                                                            }>
                                                            מחק</button>
                                                        <button class="close" >בטל</button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div> */}
