import * as React from "react";
import { Modal, Form, Button } from "react-bootstrap";
import { useState } from "react";
import { Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import { HiArrowNarrowLeft } from "react-icons/hi";
import BaseService from "../../../service/base.service.tsx";

const TablesEdit = () => {

    const [nameTable, setNameTable] = useState('');
    const [capacity, setCapacity] = useState('');

    const handleChange = (event) => {
        if (event.target.name == "nameTable") {
            setNameTable(event.target.value);
            console.log('value is: nameTable', event.target.value);
        }
        if (event.target.name == "capacity") {
            setCapacity(event.target.value);
            console.log('value is: capacity', event.target.value);
        }
    };

    const handleChange1 = event => {
        setCapacity(event.target.value);

        console.log('value is:', event.target.value);
    };


    const [finance, setFinance] = useState(true);
    const history = 0;

    const handleTextAreaChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setFinance((values) => ({ ...values, [name]: value }));
    };

    const onInputPickerChange = (value, fieldName) => {
        setFinance((values) => ({ ...values, [fieldName]: value }));
    }
    const onCancel = () => {
        console.log("OnCancel")
        history.goBack()
    };

    function checkform() {
        console.log("aaaaaaaaaaaaaaaaaaaaaaa", nameTable == "", capacity == 0)
        var f = document.forms["theform"];
        var cansubmit = true;
        console.log("zzzzzzzzzzzzzzz", document["0"])
        console.log("nnnnnnnnnnnnnnnnnnnn", document.forms["1"])
    }

    const onSubmit = () => {
        var body = { name: nameTable, capacity: (Number(capacity)) };
        console.log("xxxxxxxxxxxxxxxx", body)
        BaseService.create("/tables/", body).then((response) => {
            console.log(response)
        })
    }

    return (
        <>
            <Link
                to={"/tables/"}
                className="px-1"
                title="view">
                <Button variant="secondary">
                    <HiArrowNarrowLeft />
                </Button>
            </Link>
            <Form className={"theform"}>
                <div>
                <label for="nameTable">שם שולחן:</label>
                <input
                    type="text"
                    id="nameTable"
                    name="nameTable"
                    onKeyup={checkform()}
                    onChange={handleChange}
                    value={nameTable}
                />
                </div>
                <div>
                <label for="capacity">קיבולת:</label>
                <input
                    type="number"
                    id="capacity"
                    name="capacity"
                    onKeyup={checkform()}
                    onChange={handleChange}
                    value={capacity}
                />
                </div>
                <div>
                <Link
                    id="submitbutton"
                    to={"/tables/"}
                    className="px-1"
                    title="view">
                    <Button onClick={() => onSubmit()}
                    >שמירה</Button>
                </Link>
                </div>
            </Form>
        </>
    );

}

export default TablesEdit
