import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Customers } from "../models/customers";
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal, Form } from "react-bootstrap";



const CustomersIndex = (props) => {
    const { state, dispatch } = useAppState(0);

    const [, updateState] = useState(1);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listCustomers, setListCustomers] = useState([], Array(Customers));
    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);
    const [updateCounter, setUpdateCounter] = useState(0);  


    var pathname = window.location.pathname
    console.log("pathname", pathname)

    const IfButton = (props) => {
        if (props.queue == "tobesitted") {
            return (
                <div className={"text-start"}>
                    <Link
                        to={"/customers/" + props.queue + "/" + props.name}
                        className="px-1"
                        title="view">
                        <Button variant="secondary">
                            <i className="bi-eye-fill"></i>
                        </Button>
                    </Link>
                </div>

            )
        }
        if (props.queue == "sitting") {
            return (
                    <div className={"text-start"}>
                    <Link
                        to={"/customers/" + props.queue + "/" + props.GroupSeqNo}
                        className="px-1"
                        title="view">
                        <Button variant="secondary">
                            <i className="bi-eye-fill"></i>
                        </Button>
                    </Link>
                    <Link
                        to={"/customers/sitting/" + props.GroupSeqNo + "/reservation"}
                        className="px-1"
                        title="view">
                        <Button variant="secondary">
                            הזמנות
                        </Button>
                    </Link>
                    <Link
                        to={"/customers/"}
                        className="px-1"
                        title="view">
                        <Button
                            variant="warning"
                            onClick={() =>
                                BaseService.put('/customers/sitting/' + props.GroupSeqNo).then((rp) => {
                                    if (rp.Status) {
                                        toastr.success("Finance updated", rp.Data);
                                    } else {
                                        toastr.error(rp.Messages);
                                        console.log("Messages: " + rp.Messages);
                                        console.log("Exception: " + rp.Exception);
                                    }
                                    setUpdateCounter(updateCounter + 1)
                                })
                            }
                            title="delete"
                            className={"px-1 mx-2"}>גש לתשלום
                        </Button>
                    </Link>
                </div>
            )
        }
        if (props.queue == "awaitingbill") {
            return (
                <div className={"text-start"}>
                <Link
                        to={"/customers/" + props.queue + "/" + props.GroupSeqNo}
                        className="px-1"
                        title="view">
                        <Button variant="secondary">
                            <i className="bi-eye-fill"></i>
                        </Button>
                    </Link>
                <Button
                    variant="warning"
                    onClick={() =>
                        BaseService.put('/customers/awaitingBill/' + props.GroupSeqNo).then((rp) => {
                            setUpdateCounter(updateCounter + 1)
                            if (rp.Status) {
                                toastr.success("Finance updated", rp.Data);
                            } else {
                                toastr.error(rp.Messages);
                                console.log("Messages: " + rp.Messages);
                                console.log("Exception: " + rp.Exception);
                            }
                        })
                    }
                    title="delete"
                    className={"px-1 mx-2"}>סיום
                </Button>
                </div>
            )
        }
    }


    useEffect(() => {
        BaseService.getAll(pathname).then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listCustomers1 = data;
                console.log("isReady1", listCustomers1,);
                setIsReady(true);
                setListCustomers(listCustomers1);
                setText("fff-ll");
                console.log("isReady2---", isReady, listCustomers1, listCustomers, state);
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);


    return (
        <>
            <div>
                <h3 className="text-center">קבוצות</h3>
                <Link to={"/customers/tobesitted/add"} className="btn btn-primary">
                    <span className="px-2">
                        <i className="bi-plus-circle-fill"></i>
                    </span>
                    <span>קבוצה חדשה</span>
                </Link>
                <Link
                    to={"/customers/"}
                    className="px-1"
                    title="view">
                    <Button
                        variant="warning"
                        onClick={() =>
                            BaseService.put('/customers/tobesitted/').then((rp) => {
                                console.log("Messages: " + rp.Data);
                                if (rp.Status) {
                                    let data = rp.Data
                                    toastr.success("Finance updated", rp.Data);
                                } else {
                                    toastr.error(rp.Messages);
                                    console.log("Messages: " + rp.Messages);
                                    console.log("Exception: " + rp.Exception);
                                }
                                setUpdateCounter(updateCounter + 1)
                            })
                        }
                        title="delete"
                        className={"px-1 mx-2"}>מצא שולחן
                    </Button>
                </Link>
                <table className="table table-dark">
                    <thead>
                        <tr>
                            <th>שם קבוצה</th>
                            <th>גודל קבוצה</th>
                            <th>שעת הגעה</th>
                            <th>שולחן</th>
                            <th>סטטוס</th>
                            <th>סך לתשלום</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listCustomers.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center" >
                                        <td>{item.name}</td>
                                        <td>{item.size}</td>
                                        <td>{item.GroupSeqNo}</td>
                                        <td>{item.table || "מחכים לשולחן"}</td>
                                        <td>{item.queue}</td>
                                        <td>{item.priceAll ? item.priceAll + "₪" : "-----"}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <IfButton queue={item.queue} GroupSeqNo={item.GroupSeqNo} name={item.name}/>
                                            </div>
                                        </td>
                                    </tr>
                                );
                            })}
                    </tbody>
                </table>
                <Modal show={show} onHide={handleClose}>
                                                    <Modal.Header closeButton>
                                                        <Modal.Title>מחיקת מנה</Modal.Title>
                                                    </Modal.Header>
                                                    <Modal.Body> ?{nameForDelete} בטוח שאתה רוצה למחוק את </Modal.Body>
                                                    <Modal.Footer>
                                                        <Button variant="primary"
                                                            className="col btn btn-danger m-2 p-2 text-whait align-self-end"
                                                            onClick={() =>
                                                                BaseService.delete(pathname + "/" + categoryForDelete + "/", {
                                                                    id: idForDelete,
                                                                }).then((rp) => {
                                                                    console.log("rp", rp)
                                                                    if (rp.Status) {
                                                                        const listMenusUpdated = listMenus.filter(
                                                                            (menus) => menus.id !== idForDelete
                                                                        );
                                                                        setListMenus(listMenusUpdated);
                                                                        console.log("listMenusUpdated", listMenusUpdated);
                                                                    } else {
                                                                        toastr.error(rp.Messages);
                                                                        console.log("Messages: " + rp.Messages);
                                                                        console.log("Exception: " + rp.Exception);
                                                                    }
                                                                    handleClose()
                                                                })
                                                            }>
                                                            מחק
                                                        </Button>
                                                        <Button variant="secondary"
                                                            onClick={handleClose}
                                                            className="col btn btn-success m-2 p-2 text-whait align-self-end">
                                                            בטל
                                                        </Button>
                                                    </Modal.Footer>
                                                </Modal>
            </div>
        </>
    );
};

export default CustomersIndex;
