import * as React from "react";
import { Modal, Form } from "react-bootstrap";
import { useHistory, useState } from "react-router-dom";

import { Spinner } from "react-bootstrap";
import { TenantPickerField } from "../../../common/components/Forms/TenantPicker/TenantPickerField";
import { FormInputPicker } from "../../../common/components/Forms/FormComponents/FormInputPicker"
import { FormFreeTextField } from "../../../common/components/Forms/FormComponents/FormFreeTextField"
import { FormEditText } from "../../../common/components/Forms/FormComponents/FormEditText"
import { FormFooter } from "../../../common/components/Forms/FormComponents/FormFooter"
import { buildingFinance } from "../../../modules/building/models/BuildingDataMockManager"

import { EnumType } from "../../enum/models/EnumManager";

const TablesEdit = () => {


    const [finance, setFinance] = useState(buildingFinance);
    const history = useHistory()

    const handleTextAreaChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setFinance((values) => ({ ...values, [name]: value }));
      };
    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setFinance((values) => ({ ...values, [name]: value }));
    };
    const onInputPickerChange = (value, fieldName) => {
        setFinance((values) => ({ ...values, [fieldName]: value }));
    }
    const onCancel = () => {
        console.log("OnCancel")
        history.goBack()
    };

    return (
        <>


            <Modal style={{ direction: "rtl" }} show={true} size="lg" onHide={onCancel} >
                <Modal.Header closeButton>
                    <Modal.Title className='FormLabelHeader'>עריכת שולחנות</Modal.Title>
                </Modal.Header>
                <Modal.Body >
                    <Form className="BaseForm">
                        <FormInputPicker title="תשלום לדירה" width="600px" initialValue={buildingFinance.relativePayment || ""} onChange={onInputPickerChange} type={EnumType.RelativePayment} fieldName="relativePayment" ></FormInputPicker>
                        <FormInputPicker title="אופן גבייה" width="600px" initialValue={buildingFinance.chargeMethod || ""} onChange={onInputPickerChange} type={EnumType.PaymentMethod} fieldName="chargeMethod" ></FormInputPicker>
                        <FormInputPicker title="תדירות גבייה" width="600px" initialValue={buildingFinance.chargeFrequency || ""} onChange={onInputPickerChange} type={EnumType.Frequency} fieldName="chargeFrequency" ></FormInputPicker>
                        <FormInputPicker title="תדירות תשלום אלינו" width="600px" initialValue={buildingFinance.ourPaymentFrequency || ""} onChange={onInputPickerChange} type={EnumType.Frequency} fieldName="ourPaymentFrequency" ></FormInputPicker>
                        <FormInputPicker title="אופן תשלום" width="600px" initialValue={buildingFinance.ourPaymentMethod || ""} onChange={onInputPickerChange} type={EnumType.PaymentMethod} fieldName="ourPaymentMethod" ></FormInputPicker>
                        <TenantPickerField title="נציג משלם" width="600px" value="" fieldName="type" onChange={() => { }}></TenantPickerField>
                        <TenantPickerField title="מורשי חתימה" width="600px" value="" fieldName="type" onChange={() => { }}></TenantPickerField>
                        <FormInputPicker title="סוג חשבון" width="600px" initialValue={buildingFinance.accountType || ""} onChange={onInputPickerChange} type={EnumType.PaymentMethod} fieldName="accountType" ></FormInputPicker>
                        <FormFreeTextField title="בנק" value={buildingFinance.bankName ?? ""} width="100px" onChange={handleChange} inputType="number"></FormFreeTextField>
                        <FormFreeTextField title="סניף" value={(buildingFinance.bankBranch || 0)?.toString() ?? ""} width="100px" onChange={handleChange} inputType="number"></FormFreeTextField>
                        <FormFreeTextField title="מס׳ חשבון" value={(buildingFinance.bankAccountNumber || 0)?.toString() ?? ""} width="150px" onChange={handleChange} inputType="number"></FormFreeTextField>
                        <FormEditText title="הערות" value={buildingFinance.comments ?? ""} width="600px" height="200px" onChange={handleTextAreaChange} ></FormEditText>
                        <FormFooter okButtonTitle="שמירה" didClickCancelButton={onCancel} didClickOKButton={onSubmit}></FormFooter>
                    </Form>
                </Modal.Body>
            </Modal>


            {isLoading && (
                <Spinner animation="border" role="status">
                    <span className="visually-hidden">Loading...</span>
                </Spinner>
            )}
        </>
    );

}

export default TablesEdit
