import * as React from "react";

import { Table } from "react-bootstrap";
import { useAppState } from "../../../service/AppStateContext.tsx";
import BaseService from "../../../service/base.service.tsx";
import { Menus } from "../models/manu";
import { useState, useEffect } from "react";
import { GoBack } from "../../../defultComponent/funPublik";
import { Category } from "./inum.component";
import { list } from "./index.component";

export const OpenModalMenu = () => {
    let modal = document.getElementById("MenusViewModal").style.display = "block";
}

export const MenusView = (props) => {

    let listMenus = list()
    const closeModal = (props) => {
        let modal = document.getElementById("MenusViewModal").style.display = "none"
    }


    const [erorr, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [menu, setMenus] = useState({}, Menus);

    const path = window.location.pathname;
    const splited = path.split("/");
    const id = splited[splited.length - 1]
    // const category = splited[splited.length - 2]

    const { state } = useAppState();

    useEffect(() => {
        console.group("ssss", state);
        BaseService.fetch(`/menu/${id}`).then(
            (response) => {
                setIsLoaded(true);
                setMenus(response.data[0], Menus);
                console.log("response.data", response.data);
                console.log("menu Daqta", menu);
                console.groupEnd();
            },
            (error) => {
                setIsLoaded(true);
                setError(error);
            }
        );
    }, [state]);
    return (
        <>
            <div id="MenusViewModal" class="modal">

                <div class="modal-content">
                    <GoBack />
                    <Table className="table table-dark">
                        <tbody>
                            <tr>
                                <td>id</td>
                                <td>{menu.id}</td>
                            </tr>
                            <tr>
                                <td>שם מנה</td>
                                <td>{menu.name}</td>
                            </tr>
                            <tr>
                                <td>קטגוריה</td>
                                <td>{Category(menu.category)}</td>
                            </tr>
                            <tr>
                                <td>מחיר</td>
                                <td>{menu.price}</td>
                            </tr>
                        </tbody>
                    </Table>
                </div>
            </div>
        </>
    );
};
export default MenusView;
