import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Customers } from "../models/customers";
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal, Form } from "react-bootstrap";



const CustomersIndex = (props) => {
    const { state, dispatch } = useAppState();

    const [, updateState] = useState(1);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listCustomers, setListCustomers] = useState([], Array(Customers));
    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);
    const [updateCounter, setUpdateCounter] = useState(0);
    // const [pathname, setpathname] = useState
    var pathname = window.location.pathname

    useEffect(() => {
        console.log("pathname", pathname)
        BaseService.getAll(pathname).then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listCustomers1 = data;
                console.log("isReady1", listCustomers1,);
                setIsReady(true);
                setListCustomers(listCustomers1);
                setText("fff-ll");
                console.log("isReady2---", isReady, listCustomers1, listCustomers, state);
                state.isReadyCounter = state.isReadyCounter
                    ? state.isReadyCounter + 1
                    : 1;
                forceUpdate();
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);


    const deleteItem = (ategory, name, i) => {
        confirmAlert({
            customUI: ({ onClose }) => {
                return (
                    <div className="custom-ui align-items-center">
                        <h1>Confirm Delete!</h1>
                        <p>Are you sure you want delete Customers number: {name.id}?</p>
                        <button
                            className="col btn btn-danger m-2 p-2 text-whait align-self-end"
                            onClick={onClose}>
                            No
                        </button>
                        <button
                            className="col btn btn-success m-2 p-2 text-whait align-self-end"
                            onClick={() =>
                                BaseService.delete(pathname + "/" + ategory + "/" + name, {
                                    id: name,
                                }).then((rp) => {
                                    console.log("rp", rp)
                                    if (rp.Status) {
                                        const listCustomersUpdated = listCustomers.filter(
                                            (customers) => customers.name !== name
                                        );
                                        setListCustomers(listCustomersUpdated);
                                        onClose();
                                        console.log("listCustomersUpdated", listCustomersUpdated);
                                    } else {
                                        toastr.error(rp.Messages);
                                        console.log("Messages: " + rp.Messages);
                                        console.log("Exception: " + rp.Exception);
                                    }
                                })
                            }>
                            Yes, Delete it!
                        </button>
                    </div>
                );
            },
        });
    };

    return (
        <>
        <Modal style={{ direction: "rtl" }} show={true} size="lg" >
        <Modal.Body >
            <div>
                <h3 className="text-center">תפריט</h3>
                <Link to={"/customers/add"} className="btn btn-primary">
                    <span className="px-2">
                        <i className="bi-plus-circle-fill"></i>
                    </span>
                    <span>הוספת מנה</span>
                </Link>
                <customer className="customer customer-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>שם קבוצה</th>
                            <th>גודל קבוצה</th>
                            <th>שעת הגעה</th>
                            <th>שולחן</th>
                            <th>סטטוס</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listCustomers.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center">
                                        <td>{item.name}</td>
                                        <td>{item.size}</td>
                                        <td>{item.table}</td>
                                        <td>{item.size}</td>
                                        <td>{item.queue}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <Link
                                                    to={"/customers/"+ item.queue + "/" + item.name}
                                                    className="px-1"
                                                    title="view">
                                                    <Button variant="secondary">
                                                        <i className="bi-eye-fill"></i>
                                                    </Button>
                                                </Link>

                                                <Button
                                                    variant="warning"
                                                    onClick={() => deleteItem(item.queue, item.name, i)}
                                                    title="delete"
                                                    className={"px-1 mx-2"}>
                                                    <i className="bi-trash3-fill"></i>
                                                </Button>
                                            </div>
                                        </td>
                                    </tr>
                                );
                            })}
                    </tbody>
                </customer>
            </div>
            </Modal.Body>
            </Modal>
        </>
    );
};

export default CustomersIndex;
