import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Menus } from "../models/manu";
import BaseService from "../../../service/base.service.tsx";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal } from "react-bootstrap";
import { Category } from "./inum.component";



const MenusIndexMini = (props) => {
    const { state } = useAppState();

    const [isReady, setIsReady] = useState(false);
    const [listMenus, setListMenus] = useState([], Array(Menus));
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [nameForDelete, setNameForDelete] = useState("")
    const [categoryForDelete, setCategoryForDelete] = useState("")
    const [idForDelete, setIdForDelete] = useState("")

    var pathname = window.location.pathname
    console.log("pathname", pathname)

    const ModalDelete = (name, category, id) => {
        setNameForDelete(name)
        setCategoryForDelete(category)
        setIdForDelete(id)
        console.log("name, category, id", name, category, id)
        handleShow()
    }

    useEffect(() => {
        BaseService.getAll(pathname).then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listMenus1 = data;
                console.log("isReady1", listMenus1,);
                setIsReady(true);
                setListMenus(listMenus1);
                console.log("isReady2---", isReady, listMenus1, listMenus, state);
            } else {
                setIsReady(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);


    return (
        <>
            <div class="miniNavbar">
                <div onClick={() => window.location.href="/"}>תפריט מלא</div>
                <div onClick={() => window.location.href="/"}>מנות ראשונות</div>
                <div onClick={() => window.location.href="/"}>מרקים</div>
                <div onClick={() => window.location.href="/"}>מנה עיקרית</div>
                <div onClick={() => window.location.href="/"}>קינוחים</div>
            </div>
            <MenuItem>תפריט מלא
                            <Link to="/menus"></Link>
                        </MenuItem>
                        <MenuItem>מנות ראשונות
                            <Link to="/menus/starter"></Link>
                        </MenuItem>
                        <MenuItem>מרקים
                            <Link to="/menus/Soups"></Link>
                        </MenuItem>
                        <MenuItem>מנה עיקרית
                            <Link to="/menus/MainCourse"></Link>
                        </MenuItem>
                        <MenuItem>קינוחים
                            <Link to="/menus/desserts"></Link>
                        </MenuItem>
        </>
    );
};

export default MenusIndexMini;
