import * as React from "react";
import { Button, Table } from "react-bootstrap";
import { useState } from "react";
import { Link } from "react-router-dom";
import BaseService from "../../../service/base.service.tsx";
import { GoBack } from "../../../defultComponent/funPublik";

const TablesEdit = () => {

    const [nameTable, setNameTable] = useState('');
    const [capacity, setCapacity] = useState('');

    const handleChange = (event) => {
        if (event.target.name === "nameTable") {
            setNameTable(event.target.value);
            console.log('value is: nameTable', event.target.value);
        }
        if (event.target.name === "capacity") {
            setCapacity(event.target.value);
            console.log('value is: capacity', event.target.value);
        }
    };

    const onSubmit = () => {
        var body = { name: nameTable, capacity: (Number(capacity)) };
        console.log("xxxxxxxxxxxxxxxx", body)
        BaseService.create("/tables/", body).then((response) => {
            console.log(response)
        })
        return (
            <Link to="/tables"></Link>
        )
    }

    return (
        <>
            <GoBack />
            <Table className="table table-dark">
                <div>
                    <tr>
                        <td>
                            <label for="nameTable">שם שולחן:</label>
                        </td>
                        <td>
                            <input
                                type="text"
                                id="nameTable"
                                name="nameTable"
                                onChange={handleChange}
                                value={nameTable}
                            />
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <label for="capacity">קיבולת:</label>
                        </td>
                        <td>
                            <input
                                type="number"
                                id="capacity"
                                name="capacity"
                                onChange={handleChange}
                                value={capacity}
                            />
                        </td>
                    </tr>
                </div>
            </Table>
            <div>
                <Link
                    id="submitbutton"
                    to={"/tables/"}
                    className="px-1"
                    title="view">
                    <Button disabled={nameTable === '' || capacity === ''} onClick={() => onSubmit()}
                    >שמירה</Button>
                </Link>
            </div>
        </>
    );

}

export default TablesEdit
