import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Menus } from "../models/manu";
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal, Form } from "react-bootstrap";



const MenusIndex = (props) => {
    const { state, dispatch } = useAppState();

    const [, updateState] = useState(1);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listMenus, setListMenus] = useState([], Array(Menus));
    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);
    const [updateCounter, setUpdateCounter] = useState(0);
    // const [pathname, setpathname] = useState
    var pathname = window.location.pathname
    console.log("pathname", pathname)

    useEffect(() => {
        BaseService.getAll(pathname).then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listMenus1 = data;
                console.log("isReady1", listMenus1,);
                setIsReady(true);
                setListMenus(listMenus1);
                setText("fff-ll");
                console.log("isReady2---", isReady, listMenus1, listMenus, state);
                // state.isReadyCounter = state.isReadyCounter
                //     ? state.isReadyCounter + 1
                //     : 1;
                // forceUpdate();
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);


    const deleteItem = (ategory, name, i) => {
        confirmAlert({
            customUI: ({ onClose }) => {
                return (
                    <div className="custom-ui align-items-center">
                        <h1>Confirm Delete!</h1>
                        <p>Are you sure you want delete Menus number: {name.id}?</p>
                        <button
                            className="col btn btn-danger m-2 p-2 text-whait align-self-end"
                            onClick={onClose}>
                            No
                        </button>
                        <button
                            className="col btn btn-success m-2 p-2 text-whait align-self-end"
                            onClick={() =>
                                BaseService.delete(pathname + "/" + ategory + "/" + name, {
                                    id: name,
                                }).then((rp) => {
                                    console.log("rp", rp)
                                    if (rp.Status) {
                                        const listMenusUpdated = listMenus.filter(
                                            (menus) => menus.name !== name
                                        );
                                        setListMenus(listMenusUpdated);
                                        onClose();
                                        console.log("listMenusUpdated", listMenusUpdated);
                                    } else {
                                        toastr.error(rp.Messages);
                                        console.log("Messages: " + rp.Messages);
                                        console.log("Exception: " + rp.Exception);
                                    }
                                })
                            }>
                            Yes, Delete it!
                        </button>
                    </div>
                );
            },
        });
    };

    return (
        <>
            <div>
                <h3 className="text-center">תפריט</h3>
                <Link to={"/menus/add"} className="btn btn-primary">
                    <span className="px-2">
                        <i className="bi-plus-circle-fill"></i>
                    </span>
                    <span>הוספת מנה</span>
                </Link>
                <menu className="table table-dark" style={{ backgroundColor: "#212529"}}>
                    <thead>
                        <tr>
                            <th>שם שולחן</th>
                            <th>קטגוריה</th>
                            <th>מחיר</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listMenus.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center">
                                        <td>{item.name}</td>
                                        <td>{item.category}</td>
                                        <td>{item.price}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <Link
                                                    to={"/menus/"+ item.category + "/" + item.name}
                                                    className="px-1"
                                                    title="view">
                                                    <Button variant="secondary">
                                                        <i className="bi-eye-fill"></i>
                                                    </Button>
                                                </Link>

                                                <Button
                                                    variant="warning"
                                                    onClick={() => deleteItem(item.category, item.name, i)}
                                                    title="delete"
                                                    className={"px-1 mx-2"}>
                                                    <i className="bi-trash3-fill"></i>
                                                </Button>
                                            </div>
                                        </td>
                                    </tr>
                                );
                            })}
                    </tbody>
                </menu>
            </div>
        </>
    );
};

export default MenusIndex;
