import * as React from "react";

import { Button, Table } from "react-bootstrap";
import { useAppState } from "../../../service/AppStateContext.tsx";
import BaseService from "../../../service/base.service.tsx";
import { Customers } from "../models/diners";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { GoBack } from "../../../defultComponent/funPublik";

const Price = (priceAll) => {
    if (priceAll.priceAll) {
        return (
            <div>
                {priceAll.priceAll + "₪"}
            </div>
        )
    }
    else {
        return (
            "------"
        )
    }
}

const Status = (queue) => {
    console.log("GroupSeqNo", queue)
    if (queue === "tobesited") {
        return (
            "ממתין לשולחן"
        )
    }
    if (queue === "sit") {
        return (
            "ממתין לשירות"
        )
    }
    if (queue === "awaitingbill") {
        return (
            "ממתין לחשבון"
        )
    }
}

const CustomersView = (props) => {
    const [erorr, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [customer, setCustomers] = useState({}, Customers);


    const path = window.location.pathname;
    console.log("path", path)
    const splited = path.split("/");
    const diners = splited[splited.length - 3]
    const id = splited[splited.length - 1]

    const { state } = useAppState();

    const MapResevation = (reservation) => {
        console.log("reservation", reservation)
        for (i in reservation) {
            console.log("xx", )
        }
        return (
            <menu className="table table-dark" >
                <thead>
                    <tr>
                        <th>id</th>
                        <th>שם מנה</th>
                        <th>קטגוריה</th>
                        <th>מחיר</th>
                    </tr>
                </thead>
                <tbody>
                    {reservation.map((item, i) => {
                        return (
                            <tr key={i} className="align-items-center">
                                <td>{item.id}</td>
                                <td>{item.name}</td>
                                <td>{item.category}</td>
                                <td>{item.price}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </menu>
        )
    }

    useEffect(() => {
        console.group("ssss", state);
        BaseService.fetch(`/${diners}/${id}`).then(
            (response) => {
                setIsLoaded(true);
                setCustomers(response.data[0], Customers);
                console.log("response.data", response.data[0]);
                console.log("customer Daqta", customer.reservations);
                console.groupEnd();
            },
            (error) => {
                setIsLoaded(true);
                setError(error);
            }
        );
    }, [state]);
    return (
        <>
            <GoBack />
            <Table className="table table-dark">
                <tbody>
                    <tr>
                        <td>id</td>
                        <td>{customer.id}</td>
                    </tr>
                    <tr>
                        <td>שם קבוצה:</td>
                        <td>{customer.name}</td>
                    </tr>
                    <tr>
                        <td>גודל קבוצה:</td>
                        <td>{customer.size}</td>
                    </tr>
                    <tr>
                        <td>שעת הגעה:</td>
                        <td>{customer.lastUpdated}</td>
                    </tr>
                    <tr>
                        <td>שולחן:</td>
                        <td>{customer.table1 ?
                            <Link
                                to={"/tables/" + customer.table1}
                                className="px-1"
                                title="view">
                                <Button variant="secondary">
                                    {customer.table1}
                                </Button>
                            </Link> : "מחכים לשולחן"}
                        </td>
                    </tr>
                    <tr>
                        <td>סטטוס:</td>
                        <td>{Status(customer.queue)}</td>
                    </tr>
                    <tr>
                        <td>סך לתשלום</td>
                        <td><Price priceAll={customer.sum} /></td>
                    </tr>
                    <tr>
                        <td>הזמנות:</td>
                        <td>{customer.reservations ? MapResevation(customer.reservations) : "------"}</td>
                    </tr>
                </tbody>
            </Table>
        </>
    );
};
export default CustomersView;
