import * as React from "react";
import { Modal, Form, Button, Table } from "react-bootstrap";
import { useState } from "react";
import { Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import { HiArrowNarrowLeft } from "react-icons/hi";
import BaseService from "../../../service/base.service.tsx";
import { GoBack } from "../../../defultComponent/funPublik";
// import { TenantPickerField } from "../../../common/components/Forms/TenantPicker/TenantPickerField";
// import { FormInputPicker } from "../../../common/components/Forms/FormComponents/FormInputPicker"
// import { FormFreeTextField } from "../../../common/components/Forms/FormComponents/FormFreeTextField"
// import { FormEditText } from "../../../common/components/Forms/FormComponents/FormEditText"
// import { FormFooter } from "../../../common/components/Forms/FormComponents/FormFooter"
// import { buildingFinance } from "../models/menu"

// import { EnumType } from "../../enum/models/EnumManager";

const CustomersEdit = () => {

    const [nameGroup, setNameGroup] = useState('');
    const [sizeGroup, setsizeGroup] = useState('');

    const handleChange = (event) => {
        if (event.target.name == "nameGroup") {
            setNameGroup(event.target.value);
            console.log('value is: nameGroup', event.target.value);
        }
        if (event.target.name == "sizeGroup") {
            setsizeGroup(event.target.value);
            console.log('value is: sizeGroup', event.target.value);
        }
    };

    const handleChange1 = event => {
        setsizeGroup(event.target.value);

        console.log('value is:', event.target.value);
    };


    const [finance, setFinance] = useState(true);
    const history = 0;

    const handleTextAreaChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setFinance((values) => ({ ...values, [name]: value }));
    };

    const onInputPickerChange = (value, fieldName) => {
        setFinance((values) => ({ ...values, [fieldName]: value }));
    }

    const onCancel = () => {
        console.log("OnCancel")
        history.goBack()
    };

    function checkform() {
        console.log("aaaaaaaaaaaaaaaaaaaaaaa", nameGroup == "", sizeGroup == 0)
        var f = document.forms["theform"];
        var cansubmit = true;
        console.log("zzzzzzzzzzzzzzz", document["0"])
        console.log("nnnnnnnnnnnnnnnnnnnn", document.forms["1"])
    }

    const onSubmit = () => {
        var body = { name: nameGroup, size: (Number(sizeGroup)) };
        console.log("xxxxxxxxxxxxxxxx", body)
        BaseService.create("/customers/tobesitted/", body).then((response) => {
            console.log(response)
        })
    }

    return (
        <> 
            <GoBack />
            <Table className="table table-dark">
            <div>
                    <tr>
                        <td>
                <label for="nameGroup">שם קבוצה:</label>
                </td>
                <td>
                <input
                    type="text"
                    id="nameGroup"
                    name="nameGroup"
                    onKeyup={checkform()}
                    onChange={handleChange}
                    value={nameGroup}
                />
                </td>
                </tr>
                <label for="nameGroup">גודל קבוצה:</label>
                <input
                    type="number"
                    id="sizeGroup"
                    name="sizeGroup"
                    onKeyup={checkform()}
                    onChange={handleChange}
                    value={sizeGroup}
                />
                <Link
                    id="submitbutton"
                    to={"/customers/"}
                    className="px-1"
                    title="view">
                    <Button onClick={() => onSubmit()}
                    >שמירה</Button>
                </Link>
            </div>
            </Table>
        </>
    );

}

export default CustomersEdit
