import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Menus } from "../../menu/models/manu";
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal, Form } from "react-bootstrap";
import "../../../App.css"



const MenusIreservationndex = (props) => {
    const { state, dispatch } = useAppState();

    const [, updateState] = useState(1);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listMenus, setListMenus] = useState([], Array(Menus));
    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);
    const [updateCounter, setUpdateCounter] = useState(0);
    const [listItem, setListItem] = useState([], Array(Menus));
    const [isReadyRes, setIsReadyRes] = useState(0);
    // const [pathname, setpathname] = useState
    var pathname = window.location.pathname
    console.log("pathname", pathname)

    useEffect(() => {
        BaseService.getAll("/menus").then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listMenus1 = data;
                console.log("isReady1", listMenus1,);
                setIsReady(true);
                setListMenus(listMenus1);
                setText("fff-ll");
                console.log("isReady2---", isReady, listMenus1, listMenus, state);
                // state.isReadyCounter = state.isReadyCounter
                //     ? state.isReadyCounter + 1
                //     : 1;
                // forceUpdate();
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);


    const AddToCart = (item) => {
        console.log("item", item)
        let listItem1 = listItem
        listItem1.push(item)
        setIsReadyRes(isReadyRes + 1);
        setListItem(listItem1);
        console.log("list item", listItem)

    }

    const deleteItem = (category, name, price) => {
        var newItem = []
        var count = 0
        for (let menus of listItem) {
            if (menus.name !== name && menus.category !== category && menus.price !== price || count == 1) {
                newItem.push(menus)
            }
            else {
                count = 1
            }
        }
        setIsReadyRes(isReadyRes + 1);
        setListItem(newItem);
    }

    const saveResevation = () => {
        console.log("pathnameAAAAAAAAAAAAaa", pathname)
        var body = listItem;
        console.log("xxxxxxxxxxxxxxxx", body)
        BaseService.create(pathname, body).then((response) => {
            console.log(response)
        })
    }

    return (
        <>
            <div>
                <h3 className="text-center">תפריט</h3>
                <Link to={"/menus/add"} className="btn btn-primary">
                    <span className="px-2">
                        <i className="bi-plus-circle-fill"></i>
                    </span>
                    <span>הוספת מנה</span>
                </Link>
                <menu className="menu menu-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>שם שולחן</th>
                            <th>קטגוריה</th>
                            <th>מחיר</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listMenus.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center">
                                        <td>{item.name}</td>
                                        <td>{item.category}</td>
                                        <td>{item.price}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <Link
                                                    to={"/menus/" + item.category + "/" + item.name}
                                                    className="px-1"
                                                    title="view">
                                                    <Button variant="secondary">
                                                        {/* <i className="bi-eye-fill"></i> */}
                                                    </Button>
                                                </Link>

                                                <Button
                                                    variant="warning"
                                                    onClick={() => AddToCart(item)}
                                                    title="delete"
                                                    className={"px-1 mx-2"}>
                                                    הוספה לסל
                                                </Button>
                                            </div>
                                        </td>
                                    </tr>
                                );
                            })}
                    </tbody>
                </menu>
                <h3 className="text-center">הזמנות</h3>
                <Link
                    to={"/customers"}
                    className="px-1"
                    title="view">

                    <Button
                        variant="warning"
                        onClick={() => saveResevation()}
                        title="delete"
                        className={"px-1 mx-2"}>
                        שמירה
                    </Button>
                </Link>
                <menu className="menu menu-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>שם שולחן</th>
                            <th>קטגוריה</th>
                            <th>מחיר</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReadyRes &&
                            listItem.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center">
                                        <td>{item.name}</td>
                                        <td>{item.category}</td>
                                        <td>{item.price}</td>
                                        <Button
                                            variant="warning"
                                            onClick={() => deleteItem(item.category, item.name, item.price)}
                                            title="delete"
                                            className={"px-1 mx-2"}>
                                            <i className="bi-trash3-fill"></i>
                                        </Button>
                                    </tr>
                                );
                            })}
                    </tbody>
                </menu>
            </div>
        </>
    );
};

export default MenusIreservationndex;
