import * as React from "react";
import "../../../App.css";
// import ExampleFinaceTableRow from "./TableRow";
// import { FaHeart } from "react-icons/fa";

import { Link /*,useRouteMatch/*/ } from "react-router-dom";
import { Tables } from "../models/table"
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Form } from "react-bootstrap";


const TablesIndex = (props) => {
    const { state, dispatch } = useAppState();
    // let { path, url } = useRouteMatch();

    const [, updateState] = useState(1);

    const [isLoading, setIsLoading] = useState(false);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listTables, setListTables] = useState([], Array(Tables));
    let listTables1 = Tables;

    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);

    const setState = (param) => { };
    const [updateCounter, setUpdateCounter] = useState(0);
    const updateCounterToTriggerRender = () => {
        setUpdateCounter(updateCounter + 1);
    };

    useEffect(() => {
        BaseService.getAll("/tables").then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listTables1 = data;

                console.log("isReady1", listTables1, );

                setIsReady(true);
                setListTables(listTables1);

                setText("fff-ll");

                console.log("isReady2---", isReady, listTables1, listTables, state);
                state.isReadyCounter = state.isReadyCounter
                    ? state.isReadyCounter + 1
                    : 1;

                forceUpdate();

                // setState({ listTabless: listTabless });
                // setState({ isReady: true });
            } else {
                setIsReady(true);
                setHasError(true);

                // setState({ isReady: true });
                // setState({ hasError: true });
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state]);

    const deleteItem = (object, i) => {
        confirmAlert({
            customUI: ({ onClose }) => {
                return (
                    <div className="custom-ui align-items-center">
                        <h1>Confirm Delete!</h1>
                        <p>Are you sure you want delete Tables number: {object}?</p>
                        <button
                            className="col btn btn-danger m-2 p-2 text-whait align-self-end"
                            onClick={onClose}>
                            No
                        </button>
                        <button
                            className="col btn btn-success m-2 p-2 text-whait align-self-end"
                            onClick={() =>
                                fetch(`http://127.0.0.1:4000/tables/${object}`, { method: 'DELETE' })
                                .then(async response => {
                                    const data = await response.json();
                                    co

                                    // check for error response
                                    if (!response.ok) {
                                        // get error message from body or default to response status
                                        const error = (data && data.message) || response.status;
                                        return Promise.reject(error);
                                    }

                                    setStatus('Delete successful');
                                })
                                .catch(error => {
                                    setErrorMessage(error);
                                    console.error('There was an error!', error);
                                })}>
                            Yes, Delete it!
                        </button>
                    </div>
                );
            },
        });
    };

    return (
        <>
            <div>
                <h3 className="text-center">שולחנות</h3>
                <table className="table table-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>name</th>
                            <th>Group Seq Num</th>
                            <th>capacity</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listTables.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center">
                                        <td>{item.name}</td>
                                        <td>{item.GroupSeqNum || "פנוי"}</td>
                                        <td>{item.capacity}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <Link
                                                    to={"/tables/" + item.name}
                                                    className="px-1"
                                                    title="view">
                                                    <Button variant="secondary">
                                                        <i className="bi-eye-fill"></i>
                                                    </Button>
                                                </Link>

                                                 <Button
                                                    variant="warning"
                                                    onClick={() => deleteItem(item.name, i)}
                                                    title="delete"
                                                    className={"px-1 mx-2"}>
                                                    <i className="bi-trash3-fill"></i>
                                                </Button>  
                                            </div>
                                        </td>
                                    </tr>
                                );
                            })}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default TablesIndex;
