import React from 'react';
import { BrowserRouter, Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import SideMenu from './defultComponent/sidebar';
import RouterA from './defultComponent/router';
import { ProSidebar } from 'react-pro-sidebar';

class App extends React.Component {
  constructor(props, state) {
    super(props);
    // this.handleChange = this.handleChange.bind(this);
    // this.state = {temperature: ''};

    console.log("====", props, state);
  }
  render() {
    return (

      <BrowserRouter>
        <div style={{ backgroundColor: 'white' }} >
          <div className="row" >
            <div className="col col-3">
              <SideMenu />
            </div>
            <div className="col col-9" style={{ padding: "40px" }}>
              <div style={{ width: "100%" }}>
                <div className="col col-12">
                  <div className="content-em-1">
                    <div>
                      <Routes>
                        {/* Home */}
                        <Route path='/' />

                        {/* menu */}
                        <Route path='/menu' />
                      </Routes>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </BrowserRouter>
    )
  }
}

export default App;


// menus
router.get('/dishes/', getAllMenu);
router.get('/dishes/:name', getByNameMenu) // להוסיף קטגוריה
router.post('/dishes/', createMenu)
router.delete('/dishes/:name', deleteMenu)
router.patch('/dishes/:name', editMenu)

// tables
router.get('/tables/', getAllTable);
router.get('/tables/:name', getByNameTable)
router.post('/tables/', createTable)
router.delete('/tables/:name', deleteTable)
// router.patch('/dishes/:name', editMenu)

// customers
router.get('/customers/', getAllCustomers);

// customers tobeSitted
router.get('/customers/tobeSitted/', getAllTobeSitted);
router.get('/customers/tobeSitted/:name', getByNameTobeSitted)
router.post('/customers/tobeSitted/', createTobeSitted)
router.delete('/customers/tobeSitted/:name', deleteTobeSitted)
router.put('/customers/tobeSitted/', nextTurnTobeSitted)

// customers Sitting
router.get('/customers/sitting/', getAllSitting);
router.get('/customers/sitting/:GroupSeqNo', getByNameSitting)
router.delete('/customers/sitting/:name', deleteSitting)
router.put('/customers/sitting/:GroupSeqNo', nextTurnSitting) 

// reservation
router.get('/customers/sitting/:GroupSeqNo/reservation', getAllReservation)
router.get('/customers/sitting/:GroupSeqNo/reservation/:name', getByNameReservation)
// router.delete('/customers/sitting/:GroupSeqNo/reservation/:name', deleteR)
// router.patch('/customers/sitting/:GroupSeqNo/reservation', editReservation)

// customers Awaiting Bill
router.get('/customers/awaitingBill/', getAllAwaitingBill);
router.get('/customers/awaitingBill/:name', getByNameAwaitingBill)
router.put('/customers/awaitingBill/:name', nextTurn)
