import * as React from "react";
import { Link } from "react-router-dom";
import { Menus } from "../../menu/models/manu";
import BaseService from "../../../service/base.service.tsx";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useState } from "react";
import "../../../App.css";



const MenusIreservationndex = (props) => {
    const { state } = useAppState();

    const [isReady, setIsReady] = useState(false);
    const [listMenus, setListMenus] = useState([], Array(Menus));
    const [listItem, setListItem] = useState([], Array(Menus));
    const [isReadyRes, setIsReadyRes] = useState(0);

    var pathname = window.location.pathname
    console.log("pathname", pathname)

    useEffect(() => {
        BaseService.getAll("/menus").then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listMenus1 = data;
                console.log("isReady1", listMenus1,);
                setIsReady(true);
                setListMenus(listMenus1);
                console.log("isReady2---", isReady, listMenus1, listMenus, state);
            } else {
                setIsReady(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);


    const AddToCart = (item) => {
        console.log("item", item)
        let listItem1 = listItem
        listItem1.push(item)
        setIsReadyRes(isReadyRes + 1);
        setListItem(listItem1);
        console.log("list item", listItem)

    }

    const deleteItem = (category, name, price) => {
        var newItem = []
        var count = 0
        for (let menus of listItem) {
            if (menus.name !== name && menus.category !== category && menus.price !== price || count === 1) {
                newItem.push(menus)
            }
            else {
                count = 1
            }
        }
        setIsReadyRes(isReadyRes + 1);
        setListItem(newItem);
    }

    const saveResevation = () => {
        var body = listItem;
        console.log("body", body)
        BaseService.create(pathname, body).then((response) => {
            console.log(response)
        })
    }

    return (
        <>
        <div className="flex-container">
            <div>
                <h3 className="text-center">הזמנות</h3>
                <menu className="table table-dark">
                    <thead>
                        <tr>
                            <th>שם שולחן</th>
                            <th>קטגוריה</th>
                            <th>מחיר</th>
                            <th className="text-center">Action</th>
                            </tr>
                            </thead>
                            <tbody>
                                {isReadyRes &&
                                listItem.map((item, i) => {
                                    return(
                                    <tr key={i} className="align-items-center">
                                        <td>{item.name}</td>
                                        <td>{item.category}</td>
                                        <td>{item.price}</td>
                                        <td>
                                            <Button
                                            variant="warning"
                                                    onClick={() => deleteItem(item.category, item.name, item.price)}
                                                    title="delete"
                                                    className={"px-1 mx-2"}>
                                                        <i className="bi-trash3-fill"></i>הסר
                                                </Button>
                                            </td>
                                        </tr>
                                    );
                                })}
                        </tbody>
                    </menu>
                </div>
                <div>
                    <h3 className="text-center">תפריט</h3>
                    <menu className="table table-dark" style={{ position: "right" }}>
                        <thead>
                            <tr>
                                <th>שם מנה</th>
                                <th>קטגוריה</th>
                                <th>מחיר</th>
                                <th className="text-center">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {isReady &&
                                listMenus.map((item, i) => {
                                    return (
                                        <tr key={i} className="align-items-center">
                                            <td>{item.name}</td>
                                            <td>{item.category}</td>
                                            <td>{item.price}</td>
                                            <td>
                                                <div className={"text-start"}>
                                                    <Button
                                                        variant="warning"
                                                        onClick={() => AddToCart(item)}
                                                        title="delete"
                                                        className={"px-1 mx-2"}>
                                                        הוספה לסל
                                                    </Button>
                                                </div>
                                            </td>
                                        </tr>
                                    );
                                })}
                        </tbody>
                    </menu>
                    <Link
                        to={"/customers"}
                        className="px-1"
                        title="view">
                        <Button
                            variant="warning"
                            onClick={() => saveResevation()}
                            title="delete"
                            className={"px-1 mx-2"}>
                            שמירה
                        </Button>
                    </Link>
                </div>
            </div>
        </>
    );
};

export default MenusIreservationndex;
