                        <img src="https://img.icons8.com/ios-glyphs/30/ab5e2a/visible--v1.png" />
                    </button>
                    <CustomersView />
                </div>
            )
        }
        if (props.item.queue === "sit") {
            return (
                <div class="icons">
                    {/* <View {...props} /> */}
                    <button id="myBtn" onClick={() => ModalDelete(props.item)}>
                        <img src="https://img.icons8.com/ios-glyphs/30/ab5e2a/visible--v1.png" />
                    </button>
                    <CustomersView />
                    <Reservation {...props} />
                    <Payment {...props} />
                </div>
            )
        }
        if (props.item.queue === "awaitingbill") {
            return (
                <div class="icons">
                    {/* <View {...props} /> */}
                    <button id="myBtn" onClick={() => ModalDelete(props.item)}>
                        <img src="https://img.icons8.com/ios-glyphs/30/ab5e2a/visible--v1.png" />
                    </button>
                    <CustomersView />
                    <Completion {...props} />
                </div>
            )
        }
    }




