                    </tbody>
                </table>
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>תוצאות חיפוש</Modal.Title>
                    </Modal.Header>
                    <Modal.Body> {responsServerFun(responsServer)} </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary"
                            onClick={() => window.location.reload()}
