                <table>
                    <tr>
                        <th>Action</th>
                        <th>סך לתשלום</th>
                        <th>סטטוס</th>
                        <th>שולחן</th>
                        <th>שעת הגעה</th>
                        <th>גודל קבוצה</th>
                        <th>שם קבוצה</th>
                        <th>id</th>
                    </tr>
                    {isReady &&
                        listCustomers.map((item, i) => {
                            // console.log("item", item)
                            return (
                                <tr key={i}>
                                    <td>
                                        <div >
                                            <IfButton item={item} activ="CustomersView" />
                                        </div>
                                    </td>
                                    <td>{item.sum ? item.sum + "₪" : "-----"}</td>
