import * as React from "react";

import { Button, Table } from "react-bootstrap";
import { useAppState } from "../../../service/AppStateContext.tsx";
import BaseService from "../../../service/base.service.tsx";
import { Customers } from "../models/customers";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { HiArrowNarrowLeft } from "react-icons/hi";

const CustomersView = (props) => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [customer, setCustomers] = useState({}, Customers);


    const path = window.location.pathname;
    const splited = path.split("/");
    const name = splited[splited.length - 1]
    const category = splited[splited.length - 2]

    const { state, dispatch } = useAppState();

    useEffect(() => {
        console.group("ssss", state);
        BaseService.fetch(`/customers/${category}/${name}`).then(
            (response) => {
                setIsLoaded(true);
                setCustomers(response.data, Customers);
                console.log("response.data", response.data);
                console.log("customer Daqta", customer);
                console.groupEnd();
            },
            (error) => {
                setIsLoaded(true);
                setError(error);
            }
        );
    }, [state]);
    return (
        <>      {
            <Table striped bordered hover>
                <Link
                    to={"/customers/"`${category}`}
                    className="px-1"
                    title="view">
                    <Button variant="secondary">
                        <HiArrowNarrowLeft />
                    </Button>
                </Link>
                <tbody>
                    <tr>
                        <td>שם מנה</td>
                        <td>{customer.name}</td>
                    </tr>
                    <tr>
                        <td>קטגוריה</td>
                        <td>{customer.category }</td>
                    </tr>
                    <tr>
                        <td>מחיר</td>
                        <td>{customer.price}</td>
                    </tr>
                </tbody>
            </Table>
        }
        </>
    );
};
export default CustomersView;
