import * as React from "react";

import { Table } from "react-bootstrap";
import Form from "react-bootstrap/Form";
import { useAppState } from "../../../service/AppStateContext.tsx";
import BaseService from "../../../service/base.service.tsx";
import { Tables } from "../models/table";
import { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import { Link/*, useRouteMatch*/ } from "react-router-dom";
// import { TablesNavbar } from "./navbarFinance";

const TablesView = (props) => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [table, setTables] = useState({}, Tables);


    const path = window.location.pathname;
    const splited = path.split("/");
    const x = splited[splited.length - 1]
    console.log("qqqqqqq", x)
    const { state, dispatch } = useAppState();
    //   const buildingId = state.buildingId;

    //   let { path, url } = useRouteMatch();

    // ---------------------------------------------------------
    // ---------------------------------------------------------
    // Get the id from the route
    // ---------------------------------------------------------
    // ---------------------------------------------------------
    let id = "0";
    let financeId = id ? id : "0";
    try {
        let id = props.match.params.id;
        financeId = id;
        console.log("---id----", id);
    } catch (e) {
        console.log(e);
    }

    //   const setTablesId = (id: string) => {
    //     financeId = id;
    //   };

    useEffect(() => {
        console.group("ssss", state);
        if (financeId) {
            BaseService.fetch(`/tables/${x}`).then(
                (response) => {
                    setIsLoaded(true);
                    setTables(response.data, Tables);
                    console.log("response.data", response.data);
                    console.log("table Daqta", table);
                    console.groupEnd();
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            );
        }
    }, [state]);

    console.log("---props", props, state);

    return (
        <>      {
            <Table striped bordered hover>
                <tbody>
                    <tr>
                        <td>שם שולחן</td>
                        <td>{table.name}</td>
                    </tr>
                    <tr>
                        <td>סטטוס</td>
                        <td>{table.GroupSeqNum || "פנוי"}</td>
                    </tr>
                    <tr>
                        <td>קיבולת</td>
                        <td>{table.capacity}</td>
                    </tr>
                </tbody>
            </Table>
        }
        </>
    );
};
export default TablesView;
