                                            title="view">
                                            <Button variant="secondary">
                                                {item.table1}
                                            </Button>
                                        </Link> : "מחכים לשולחן"}
                                    </td>
                                    <td> {item.lastUpdated} </td>
                                    <td>{item.size}</td>
                                    <td>{item.name}</td>
                                    <td>{item.id}</td>
                                </tr>
                            );
                        })}
                </table>
            </div>
        </>
    );
};

export default CustomersIndex;
