import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Tables } from "../models/table"
import BaseService from "../../../service/base.service.tsx";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useState } from "react";
import * as toastr from "toastr";
import { Modal } from "react-bootstrap";
import CustomersView, { OpenModal } from "../../diners/components/view.component"
import { list } from "../../diners/components/index.component";

// let IdForDelete
const TablesIndex = () => {
    const { state } = useAppState();

    const [isReady, setIsReady] = useState(false);
    const [listTables, setListTables] = useState([], Array(Tables));
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [idForDelete, setIdForDelete] = useState("")

    const OpenModalDelete = () => {
        let modal = document.getElementById("TableDelete").style.display = "block";
    }
    const closeModalDelete = () => {
        let modal = document.getElementById("TableDelete").style.display = "none"
    }

    const ModalDelete = (id) => {
        setIdForDelete(id)
        console.log(id)
        OpenModalDelete()
    }



    let newListTables = []
    let stap = true
    let c = 0
    let temp = []
    while (stap) {

        if (temp.length == 3) {
            newListTables.push(temp)
            temp = []
        }
        temp.push(listTables[c])
        c += 1
        if (typeof (listTables[c]) == "undefined") {
            if (temp.length > 0) {
                newListTables.push(temp)
            }
            stap = false
        }
    }

    const status = (id) => {
        console.log(id)
        BaseService.get(`/diners/`, id).then((rp) => {
            if (rp.Status) {
                let data = rp.Data;
                console.log("data", data)
                CustomersView(data)
                OpenModal()
            } else {
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });

    }


    useEffect(() => {
        BaseService.getAll("/tables").then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listTables1 = data;
                console.log("isReady1", listTables1,);
                setIsReady(true);

                setListTables(listTables1);
                console.log("isReady2---", isReady, listTables1, listTables, state);
            } else {
                setIsReady(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state]);



    return (
        <>
            <div class="map">
                {newListTables.map((item, j) => {
                    return (
                        <div class="mapA">
                            {isReady &&
                                newListTables[j].map((item, i) => {
                                    return (
                                        <div key={i} >
                                            <div class="mapTable">
                                                <h2 >{item.id}</h2>
                                                <tr class="trTable">
                                                    <td >סטטוס</td>
                                                    <td >{item.status ? item.status : "פנוי"}
                                                        {/*  <button onClick={() => status(item.status)}>
                                                        {item.status}
                                                     </button> */}
                                                        {/* <CustomersView /> */}
                                                    </td>
                                                </tr>
                                                <tr class="trTable">
                                                    <td >קיבולת</td>
                                                    <td >{item.capacity}</td>
                                                </tr>
                                                <tr class="trTable">
                                                    <td >Action</td>
                                                    <td >
                                                        <div className={"text-start"}>
                                                            <Link
                                                                to={"/tables/" + item.id}
                                                                className="px-1"
                                                                title="view">
                                                                <Button variant="secondary">
                                                                    <i className="bi-eye-fill"></i>
                                                                </Button>
                                                            </Link>


                                                            <Button variant="primary" onClick={() => ModalDelete(item.id)} >
                                                                <i className="bi-trash3-fill"></i>
                                                            </Button>
                                                            <button id="myBtn" onClick={() => ModalDelete(item.id)}>
                                                                <img src="https://img.icons8.com/material/30/ab5e2a/filled-trash.png" />
                                                            </button>
                                                            {/* <>
                                                                <Modal show={show} onHide={handleClose}>
                                                                    <Modal.Header closeButton>
                                                                        <Modal.Title>מחיקת שולחן</Modal.Title>
                                                                    </Modal.Header>
                                                                    <Modal.Body> ?{idForDelete} בטוח שאתה רוצה למחוק את </Modal.Body>
                                                                    <Modal.Footer>
                                                                        <Button variant="primary"
                                                                            className="col btn btn-danger m-2 p-2 text-whait align-self-end"
                                                                            onClick={() =>
                                                                                BaseService.delete("/tables/", {
                                                                                    id: idForDelete,
                                                                                }).then((rp) => {
                                                                                    console.log("rp", rp)
                                                                                    if (rp.Status) {
                                                                                        const listTablesUpdated = listTables.filter(
                                                                                            (tables) => tables.id !== idForDelete
                                                                                        );
                                                                                        setListTables(listTablesUpdated);
                                                                                        console.log("listTablesUpdated", listTablesUpdated);
                                                                                    } else {
                                                                                        toastr.error(rp.Messages);
                                                                                        console.log("Messages: " + rp.Messages);
                                                                                        console.log("Exception: " + rp.Exception);
                                                                                    }
                                                                                    handleClose()
                                                                                })
                                                                            }>
                                                                            מחק
                                                                        </Button>
                                                                        <Button variant="secondary"
                                                                            onClick={handleClose}
                                                                            className="col btn btn-success m-2 p-2 text-whait align-self-end">
                                                                            בטל
                                                                        </Button>
                                                                    </Modal.Footer>
                                                                </Modal>
                                                            </> */}
                                                            <>
                                                                <div id="TableDelete" class="modal">

                                                                    <div class="modal-content">
                                                                        <h2>מחיקת שולחן</h2>
                                                                        <p>?{idForDelete} בטוח שאתה רוצה למחוק את</p>
                                                                        <div class="icons">
                                                                            <button class="close"
                                                                                onClick={() =>
                                                                                    BaseService.delete("/tables/", {
                                                                                        id: idForDelete.id,
                                                                                    }).then((rp) => {
                                                                                        if (rp.Status) {
                                                                                            const listTablesUpdated = listTables.filter(
                                                                                                (tables) => tables.id !== idForDelete
                                                                                            );
                                                                                            setListTables(listTablesUpdated);
                                                                                            console.log("listTablesUpdated", listTablesUpdated);
                                                                                        } else {
                                                                                            toastr.error(rp.Messages);
                                                                                            console.log("Messages: " + rp.Messages);
                                                                                            console.log("Exception: " + rp.Exception);
                                                                                        }
                                                                                    })
                                                                                }
                                                                            >
                                                                                מחק</button>
                                                                            <button class="close" onClick={() => closeModalDelete()}>בטל</button>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            </>
                                                        </div>
                                                    </td>
                                                </tr>
                                            </div>
                                        </div>
                                    )
                                })}
                        </div>
                    )
                })}
            </div>
        </>
    );
};

export default TablesIndex;
