import * as React from "react";
import { Modal, Form, Button, Table } from "react-bootstrap";
import { useState } from "react";
import { Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import { HiArrowNarrowLeft } from "react-icons/hi";
import BaseService from "../../../service/base.service.tsx";
import { GoBack } from "../../../defultComponent/funPublik";
import "../../../App.css"



const MenusEdit = () => {

    const [nameMenu, setNameMenu] = useState('');
    const [category, setcategory] = useState('');
    const [price, setprice] = useState('');

    const handleChange = (event) => {
        console.log("event", event.target.value, event.target.name)
        if (event.target.name == "nameMenu") {
            setNameMenu(event.target.value);
            console.log('value is: nameMenu', event.target.value);
        }
        if (event.target.name == "category") {
            setcategory(event.target.value);
            console.log('value is: category', event.target.value);
        }
        if (event.target.name == "price") {
            setprice(event.target.value);
            console.log('value is: price', event.target.value);
        }
    };


    const [finance, setFinance] = useState(true);
    const history = 0;


    function checkform() {
        console.log("aaaaaaaaaaaaaaaaaaaaaaa", nameMenu == "", category == 0)
        var f = document.forms["theform"];
        var cansubmit = true;
    }

    const onSubmit = () => {
        var body = { name: nameMenu, category: category, price: (Number(price)) };
        console.log("xxxxxxxxxxxxxxxx", body)
        BaseService.create("/menus/", body).then((response) => {
            console.log(response)
        })
    }


    return (
        <>
            <GoBack />
            <Table className="table table-dark">
                <tr>
                    <div>
                        <td>
                            <label htmlFor="category">קטגוריה:</label>
                        </td>
                        <td>
                            {/* <select class="form-control" aria-label="Default select example" onChange={handleChange} name="category">
                                <option class="dropdown-btn" >cjr</option>
                                <option value="starter">מנות ראשונות</option>
                                <option value="Soups">מרקים</option>
                                <option value="desserts">קינוחים</option>
                                <option value="MainCourse">מנה עיקרית</option>
                            </select> */}
                            <select class="selectpicker" multiple>
                                <option>Mustard</option>
                                <option>Ketchup</option>
                                <option>Relish</option>
                            </select>

                        </td>
                    </div>
                </tr>
                <tr>
                    <div>
                        <td>
                            <label htmlFor="nameMenu">שם מנה:</label>
                        </td>
                        <td>
                            <input
                                type="text"
                                id="nameMenu"
                                name="nameMenu"
                                onKeyUp={checkform()}
                                onChange={handleChange}
                                value={nameMenu}
                            />
                        </td>
                    </div>
                </tr>
                <tr>
                    <div>
                        <td>
                            <label htmlFor="category">מחיר:</label>
                        </td>
                        <td>
                            <input
                                type="text"
                                id="price"
                                name="price"
                                onKeyUp={checkform()}
                                onChange={handleChange}
                                value={price}
                            />
                        </td>
                    </div>
                </tr>
                <tr>
                    <div>
                        <Link
                            id="submitbutton"
                            to={"/menus/"}
                            className="px-1"
                            title="view">
                            <Button onClick={() => onSubmit()}
                            >שמירה</Button>
                        </Link>
                    </div>
                </tr>
            </Table>
        </>
    );

}

export default MenusEdit
