import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Menus } from "../models/manu";
import BaseService from "../../../service/base.service.tsx";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal } from "react-bootstrap";
import { Category } from "./inum.component";



const MenusIndex = (props) => {
    const { state, dispatch } = useAppState();

    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [listMenus, setListMenus] = useState([], Array(Menus));
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [nameForDelete, setNameForDelete] = useState("")
    const [categoryForDelete, setCategoryForDelete] = useState("")
    const [idForDelete, setIdForDelete] = useState("")

    var pathname = window.location.pathname
    console.log("pathname", pathname)

    const ModalDelete = (name, category, id) => {
        setNameForDelete(name)
        setCategoryForDelete(category)
        setIdForDelete(id)
        console.log("name, category, id", name, category, id)
        handleShow() 
    }

    useEffect(() => {
        BaseService.getAll(pathname).then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listMenus1 = data;
                console.log("isReady1", listMenus1,);
                setIsReady(true);
                setListMenus(listMenus1);
                console.log("isReady2---", isReady, listMenus1, listMenus, state);
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);


    return (
        <>
            <div>
                <h3 className="text-center">תפריט</h3>
                <Link to={"/menus/add"} className="btn btn-primary">
                    <span className="px-2">
                        <i className="bi-plus-circle-fill"></i>
                    </span>
                    <span>הוספת מנה</span>
                </Link>
                <menu className="table table-dark" >
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>שם מנה</th>
                            <th>קטגוריה</th>
                            <th>מחיר</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listMenus.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center">
                                        <td>{item.id}</td>
                                        <td>{item.name}</td>
                                        <td>{Category(item.category)}</td>
                                        <td>{item.price}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <Link
                                                    to={"/menus/" + item.category + "/" + item.id}
                                                    className="px-1"
                                                    title="view">
                                                    <Button variant="secondary">
                                                        <i className="bi-eye-fill"></i>
                                                    </Button>
                                                </Link>

                                                <Button variant="primary" onClick={() => ModalDelete(item.name, item.category, item.id)} >
                                                    <i className="bi-trash3-fill"></i>
                                                </Button>
                                                <Modal show={show} onHide={handleClose}>
                                                    <Modal.Header closeButton>
                                                        <Modal.Title>מחיקת מנה</Modal.Title>
                                                    </Modal.Header>
                                                    <Modal.Body> ?{nameForDelete} בטוח שאתה רוצה למחוק את </Modal.Body>
                                                    <Modal.Footer>
                                                        <Button variant="primary"
                                                            className="col btn btn-danger m-2 p-2 text-whait align-self-end"
                                                            onClick={() =>
                                                                BaseService.delete(pathname + "/" + categoryForDelete + "/", {
                                                                    id: idForDelete,
                                                                }).then((rp) => {
                                                                    console.log("rp", rp)
                                                                    if (rp.Status) {
                                                                        const listMenusUpdated = listMenus.filter(
                                                                            (menus) => menus.id !== idForDelete
                                                                        );
                                                                        setListMenus(listMenusUpdated);
                                                                        console.log("listMenusUpdated", listMenusUpdated);
                                                                    } else {
                                                                        toastr.error(rp.Messages);
                                                                        console.log("Messages: " + rp.Messages);
                                                                        console.log("Exception: " + rp.Exception);
                                                                    }
                                                                    handleClose()
                                                                })
                                                            }>
                                                            מחק
                                                        </Button>
                                                        <Button variant="secondary"
                                                            onClick={handleClose}
                                                            className="col btn btn-success m-2 p-2 text-whait align-self-end">
                                                            בטל
                                                        </Button>
                                                    </Modal.Footer>
                                                </Modal>
                                            </div>
                                        </td>
                                    </tr>
                                );
                            })}
                    </tbody>
                </menu>
            </div>
        </>
    );
};

export default MenusIndex;
