import "../../../App.css";
import * as React from "react";
import { Link } from "react-router-dom";
import { Customers } from "../models/diners";
import BaseService from "../../../service/base.service.tsx";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useState } from "react";
import * as toastr from "toastr";
import { Modal } from "react-bootstrap";
import { responsServerFun, Status } from "./inum.component";



const CustomersIndex = (props) => {
    const { state } = useAppState(0);

    const [isReady, setIsReady] = useState(false);
    const [setHasError] = useState(false);
    const [listCustomers, setListCustomers] = useState([], Array(Customers));

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [responsServer, setResponsServer] = useState("")


    var pathname = window.location.pathname
    console.log("pathname", pathname)

    useEffect(() => {
        BaseService.getAll(pathname).then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listCustomers1 = data;
                console.log("isReady1", listCustomers1,);
                setIsReady(true);
                setListCustomers(listCustomers1);
                console.log("isReady2---", isReady, listCustomers1, listCustomers, state);
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);

    const IfButton = (props) => {
        if (props.queue === "tobesited") {
            return (
                <div className={"text-start"}>
                    <Link
                        to={"/diners/" + props.queue + "/" + props.id}
                        className="px-1"
                        title="view">
                        <Button variant="secondary" >
                            <i className="bi-eye-fill"></i>
                        </Button>
                    </Link>
                </div>

            )
        }
        if (props.queue === "sit") {
            return (
                <div className={"text-start"}>
                    <Link
                        to={"/diners/" + props.queue + "/" + props.id}
                        className="px-1"
                        title="view">
                        <Button variant="secondary">
                            <i className="bi-eye-fill"></i>
                        </Button>
                    </Link>
                    <Link
                        to={"/diners/sit/" + props.id + "/reservation"}
                        className="px-1"
                        title="view">
                        <Button variant="secondary">
                            הזמנות
                        </Button>
                    </Link>
                    <Link
                        to={"/diners/"}
                        className="px-1"
                        title="view">
                        <Button
                            variant="warning"
                            onClick={() =>
                                BaseService.put('/diners/sitting/' + props.GroupSeqNo).then((rp) => {
                                    if (rp.Status) {
                                        toastr.success("Finance updated", rp.Data);
                                    } else {
                                        toastr.error(rp.Messages);
                                        console.log("Messages: " + rp.Messages);
                                        console.log("Exception: " + rp.Exception);
                                    }
                                    window.location.reload()
                                })
                            }
                            title="delete"
                            className={"px-1 mx-2"}>גש לתשלום
                        </Button>
                    </Link>
                </div>
            )
        }
        if (props.queue === "awaitingbill") {
            return (
                <div className={"text-start"}>
                    <Link
                        to={"/diners/" + props.queue + "/" + props.GroupSeqNo}
                        className="px-1"
                        title="view">
                        <Button variant="secondary">
                            <i className="bi-eye-fill"></i>
                        </Button>
                    </Link>
                    <Button
                        variant="warning"
                        onClick={() =>
                            BaseService.put('/diners/awaitingBill/' + props.GroupSeqNo).then((rp) => {
                                if (rp.Status) {
                                    toastr.success("Finance updated", rp.Data);
                                } else {
                                    toastr.error(rp.Messages);
                                    console.log("Messages: " + rp.Messages);
                                    console.log("Exception: " + rp.Exception);
                                }
                                window.location.reload()
                            })
                        }
                        title="delete"
                        className={"px-1 mx-2"}>סיום
                    </Button>
                </div>
            )
        }
    }




