                    </span>
                    <span>קבוצה חדשה</span>
                </Link>
                <Link
                    to={"/diners/"}
                    className="px-1">
                    <Button
                        variant="warning"
                        onClick={() =>
                            BaseService.get('/diners/sitbyperiority').then((rp) => {
                                console.log("Messages: " + rp.Data);
                                if (rp.Status) {
                                    setResponsServer(rp.Data)
                                    toastr.success("Finance updated", rp.Data);
                                } else {
                                    setResponsServer(rp.Messages)
                                    toastr.error(rp.Messages);
                                    console.log("Messages: " + rp.Messages);
                                    console.log("Exception: " + rp.Exception);
                                }
                                handleShow()
                            })
                        }
                        title="find"
                        className={"px-1 mx-2"}>מצא שולחן
                    </Button>
                </Link> */}
                {/* <table className="table table-dark">
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>שם קבוצה</th>
                            <th>גודל קבוצה</th>
                            <th>שעת הגעה</th>
                            <th>שולחן</th>
                            <th>סטטוס</th>
                            <th>סך לתשלום</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listCustomers.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center" >
                                        <td>{item.id}</td>
                                        <td>{item.name}</td>
                                        <td>{item.size}</td>
                                        <td> {item.lastUpdated} </td>
                                        <td>{item.table1 ?
                                            <Link
