                <div></div>
                <table className="table table-dark">
                    <thead>
                        <tr>
                            <th className="text-center">Action</th>
                            <th>סך לתשלום</th>
                            <th>סטטוס</th>
                            <th>שולחן</th>
                            <th>שעת הגעה</th>
                            <th>גודל קבוצה</th>
                            <th>שם קבוצה</th>
                            <th>id</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listCustomers.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center" >
                                        <td>{item.sum ? item.sum + "₪" : "-----"}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <IfButton queue={item.queue} GroupSeqNo={item.lastUpdated} id={item.id} />
                                            </div>
                                        </td>
                                        <td>{Status(item.queue)}</td>
                                        <td>{item.table1 ?
                                            <Link
                                            to={"/tables/" + item.table}
                                            className="px-1"
                                            title="view">
                                                <Button variant="secondary">
                                                    {item.table1}
                                                </Button>
                                            </Link> : "מחכים לשולחן"}
                                        </td>
                                                <td> {item.lastUpdated} </td>
                                                <td>{item.size}</td>
                                                <td>{item.name}</td>
                                                    <td>{item.id}</td>
                                    </tr>
                                );
                            })}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default CustomersIndex;
