import * as React from "react";
import { Modal, Form, Button } from "react-bootstrap";
import { useState } from "react";
import { Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import { HiArrowNarrowLeft } from "react-icons/hi";
import BaseService from "../../../service/base.service.tsx";
import { GoBack } from "../../../defultComponent/funPublik";

const MenusEdit = () => {

    const [nameMenu, setNameMenu] = useState('');
    const [category, setcategory] = useState('');
    const [price, setprice] = useState('');

    const handleChange = (event) => {
        if (event.target.name == "nameMenu") {
            setNameMenu(event.target.value);
            console.log('value is: nameMenu', event.target.value);
        }
        if (event.target.name == "category") {
            setcategory(event.target.value);
            console.log('value is: category', event.target.value);
        }
        if (event.target.name == "price") {
            setprice(event.target.value);
            console.log('value is: price', event.target.value);
        }
    };

    const handleChange1 = event => {
        setcategory(event.target.value);

        console.log('value is:', event.target.value);
    };


    const [finance, setFinance] = useState(true);
    const history = 0;

    const handleTextAreaChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setFinance((values) => ({ ...values, [name]: value }));
    };
    const onInputPickerChange = (value, fieldName) => {
        setFinance((values) => ({ ...values, [fieldName]: value }));
    }
    const onCancel = () => {
        console.log("OnCancel")
        history.goBack()
    };

    function checkform() {
        console.log("aaaaaaaaaaaaaaaaaaaaaaa", nameMenu == "", category == 0)
        var f = document.forms["theform"];
        var cansubmit = true;
        console.log("zzzzzzzzzzzzzzz", document["0"])
        console.log("nnnnnnnnnnnnnnnnnnnn", document.forms["1"])
    }

    const onSubmit = () => {
        var body = { name: nameMenu, category: category, price: (Number(price)) };
        console.log("xxxxxxxxxxxxxxxx", body)
        BaseService.create("/menus/", body).then((response) => {
            console.log(response)
        })
    }


    return (
        <>
            <GoBack />
            <Form className={"theform"}>
                <div>
                    <label for="nameMenu">שם מנה:</label>
                    <input
                        type="text"
                        id="nameMenu"
                        name="nameMenu"
                        onKeyup={checkform()}
                        onChange={handleChange}
                        value={nameMenu}
                    />
                </div>
                <div>
                    <label for="nameMenu">קטגוריה:</label>
                    <input
                        type="text"
                        id="category"
                        name="category"
                        onKeyup={checkform()}
                        onChange={handleChange}
                        value={category}
                    />
                </div>
                <select></select>
                <div>
                    <label for="category">מחיר:</label>
                    <input
                        type="text"
                        id="price"
                        name="price"
                        onKeyup={checkform()}
                        onChange={handleChange}
                        value={price}
                    />
                </div>
                <div>
                    <Link
                        id="submitbutton"
                        to={"/menus/"}
                        className="px-1"
                        title="view">
                        <Button onClick={() => onSubmit()}
                        >שמירה</Button>
                    </Link>
                </div>
            </Form>
        </>
    );

}

export default MenusEdit
