import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Tables } from "../models/table"
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal, Form } from "react-bootstrap";


const TablesIndex = (props) => {
    const { state, dispatch } = useAppState();

    const [, updateState] = useState(1);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listTables, setListTables] = useState([], Array(Tables));
    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);
    const [updateCounter, setUpdateCounter] = useState(0);

    useEffect(() => {
        BaseService.getAll("/tables").then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listTables1 = data;
                console.log("isReady1", listTables1,);
                setIsReady(true);
                setListTables(listTables1);
                setText("fff-ll");
                console.log("isReady2---", isReady, listTables1, listTables, state);
                state.isReadyCounter = state.isReadyCounter
                    ? state.isReadyCounter + 1
                    : 1;
                forceUpdate();
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state]);


    const deleteItem = (object, i) => {
        return(
            <Modal style={{direction:"rtl"}}  show={true} size="lg" onHide={onCancel} >
          <Modal.Header closeButton>
              <Modal.Title className='FormLabelHeader'>{formTitle}</Modal.Title>
          </Modal.Header>
          <Modal.Body >
            <Form className="BaseForm">
              <FormFreeTextField title="שם" value={provider.name ?? ""} width="300px" onChange={handleChange} inputType="text" fieldName="name"></FormFreeTextField>
              <FormFreeTextField title="דוא״ל" value={provider.email ?? ""}  width="300px" onChange={handleChange} inputType="email" fieldName="email"></FormFreeTextField>
              <FormFreeTextField title="מספר טלפון" value={provider.phoneNumber ?? ""}  width="200px" onChange={handleChange} inputType="phoneNumber" fieldName="phoneNumber"></FormFreeTextField>
              <FormInputPicker title="סוג" width="600px" initialValue={provider.type || ""} type={EnumType.ServiceProviderType} fieldName="type" onChange={onTypeChange} ></FormInputPicker>
              <FormInputCheckBox title="פעיל" width="200px" onChange={handleChangeCheckBox} value={provider.isActive ?? false} fieldName="isActive" ></FormInputCheckBox>
              <FormEditText title="הערות" value={provider.comments ?? ""} width="600px" height="200px" onChange={handleTextAreaChange} ></FormEditText>
              <FormFooter okButtonTitle="שמירה" didClickCancelButton={onCancel} didClickOKButton={onSubmit}></FormFooter>
            </Form>
          </Modal.Body>
        </Modal>
        )
    }
    //     confirmAlert({
    //         customUI: ({ onClose }) => {
    //             return (
    //                 <div class="modal-body">
    //                 <div className="custom-ui align-items-center">
    //                     <h1>Confirm Delete!</h1>
    //                     <p>Are you sure you want delete Tables number: {object.id}?</p>
    //                     <button
    //                         className="col btn btn-danger m-2 p-2 text-whait align-self-end"
    //                         onClick={onClose}>
    //                         No
    //                     </button>
    //                     <button
    //                         className="col btn btn-success m-2 p-2 text-whait align-self-end"
    //                         onClick={() =>
    //                             BaseService.delete("/tables/", {
    //                                 id: object,
    //                             }).then((rp) => {
    //                                 console.log("rp", rp)
    //                                 if (rp.Status) {
    //                                     const listTablesUpdated = listTables.filter(
    //                                         (tables) => tables.name !== object
    //                                     );
    //                                     setListTables(listTablesUpdated);
    //                                     onClose();
    //                                     console.log("listTablesUpdated", listTablesUpdated);
    //                                 } else {
    //                                     toastr.error(rp.Messages);
    //                                     console.log("Messages: " + rp.Messages);
    //                                     console.log("Exception: " + rp.Exception);
    //                                 }
    //                             })
    //                         }>
    //                         Yes, Delete it!
    //                     </button>
    //                 </div>
    //                 </div>
    //             );
    //         },
    //     });
    // };

    return (
        <>
            <div>
                <h3 className="text-center">שולחנות</h3>
                <Link to={"/tables/add"} className="btn btn-primary">
                    <span className="px-2">
                        <i className="bi-plus-circle-fill"></i>
                    </span>
                    <span>הוספת שולחן</span>
                </Link>
                <table className="table table-dark">
                    <thead>
                        <tr>
                            <th>שם שולחן</th>
                            <th>סטטוס</th>
                            <th>קיבולת</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listTables.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center">
                                        <td>{item.name}</td>
                                        <td>{item.GroupSeqNum || "פנוי"}</td>
                                        <td>{item.capacity}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <Link
                                                    to={"/tables/" + item.name}
                                                    className="px-1"
                                                    title="view">
                                                    <Button variant="secondary">
                                                        <i className="bi-eye-fill"></i>
                                                    </Button>
                                                </Link>

                                                <Button
                                                    variant="warning"
                                                    onClick={() => deleteItem(item.name, i)}
                                                    title="delete"
                                                    className={"px-1 mx-2"}>
                                                    <i className="bi-trash3-fill"></i>
                                                </Button>
                                            </div>
                                        </td>
                                    </tr>
                                );
                            })}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default TablesIndex;
