import * as React from "react";
import { useEffect, useState } from "react";

import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import { closeModal } from "../../../defultComponent/funPublik";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { Customers } from "../models/diners";
import { list } from "./index.component";

import BaseService from "../../../service/base.service.tsx";
import { Menus } from "../../menu/models/manu";



const Price = (priceAll) => {
    if (priceAll.priceAll) {
        return (
            <div>
                {priceAll.priceAll + "₪"}
            </div>
        )
    }
    else {
        return (
            "------"
        )
    }
}

const Status = (queue) => {
    if (queue === "tobesited") {
        return (
            "ממתין לשולחן"
        )
    }
    if (queue === "sit") {
        return (
            "ממתין לשירות"
        )
    }
    if (queue === "awaitingbill") {
        return (
            "ממתין לחשבון"
        )
    }
}


export const OpenModal = () => {
    let modal = document.getElementById("CustomersViewModal").style.display = "block";
}


export const CustomersView = (listA) => {

    const { state } = useAppState(0);

    const [isReady, setIsReady] = useState(false);
    const [listMenus, setListMenus] = useState([], Array(Menus));

    const MapResevation = (reservation) => {
        // reservation = JSON.parse(...reservation)
        // const object = { a: 1, b: 2, c: 3 };
        // console.log("reservation", typeof (reservation), typeof (object))

        // for (const property in reservation) {
        //     console.log(`${property}: ${reservation[property]}`);
        // }
        useEffect(() => {
            BaseService.getAll("/menu").then((rp) => {
                if (rp.Status) {
                    const data = rp.Data;
                    setIsReady(true);
                    setListMenus(data);
                } else {
                    setIsReady(true);
                    console.log("Messages: " + rp.Messages);
                    console.log("Exception: " + rp.Exception);
                }
            });
        }, [state]);

        console.log(reservation, )

        return (
            <menu className="table table-dark" >
                <thead>
                    <tr >
                        <th>id</th>
                        <th>שם מנה</th>
                        <th>קטגוריה</th>
                        <th>מחיר</th>
                    </tr>
                </thead>
                <tbody>
                    {reservation.map((item, i) => {
                        return (
                            <tr key={i} className="align-items-center">
                                <td>{item.id}</td>
                                <td>{item.name}</td>
                                <td>{item.category}</td>
                                <td>{item.price}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </menu>
        )
    }
    let listCustomers = list()



    const closeModal = (props) => {
        let modal = document.getElementById("CustomersViewModal").style.display = "none"
    }

    return (
        <>
            <div id="CustomersViewModal" class="modal">

                <div class="modal-content">
                    <h2>{listCustomers.id}</h2>
                    <div id="propertis">
                        <table>
                            <tr >
                                <td>{listCustomers.name}</td>
                                <th>שם קבוצה</th>
                            </tr>
                            <tr >
                                <td>{listCustomers.size}</td>
                                <th>גודל קבוצה</th>
                            </tr>
                            <tr >
                                <td>{listCustomers.lastUpdated}</td>
                                <th>שעת הגעה</th>
                            </tr>
                            <tr >
                                <td>{listCustomers.table1 ?
                                    <Link
                                        to={"/tables/" + listCustomers.table1}
                                        className="px-1"
                                        title="view">
                                        <Button variant="secondary">
                                            {listCustomers.table1}
                                        </Button>
                                    </Link> : "מחכים לשולחן"}
                                </td>
                                <th>שולחן</th>
                            </tr>
                            <tr >
                                <td>{Status(listCustomers.queue)}</td>
                                <th>סטטוס</th>
                            </tr>
                            <tr >
                                <td><Price priceAll={listCustomers.sum} /></td>
                                <th>סך לתשלום</th>
                            </tr>
                            <tr >
                                <td>{listCustomers.reservations
                                    ? MapResevation(listCustomers.reservations) : "------"
                                }</td>
                                <th>הזמנות</th>
                            </tr>
                        </table>
                    </div>
                    <div>
                        <button class="close" onClick={() => closeModal()}>
                            אישור
                        </button>
                    </div>
                </div>
            </div>
        </>


    )
}
export default CustomersView;
