import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Tables } from "../models/table"
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal } from "react-bootstrap";
import styles from "../../../App.css";
import { RiCloseLine } from "react-icons/ri";


const TablesIndex = (props) => {
    const { state, dispatch } = useAppState();

    const [, updateState] = useState(1);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listTables, setListTables] = useState([], Array(Tables));
    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);
    const [updateCounter, setUpdateCounter] = useState(0);
    const [isOpen, setIsOpen] = useState(false);
    const [itemName, setItemName] = useState([], Array(Tables));

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        BaseService.getAll("/tables").then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listTables1 = data;
                console.log("isReady1", listTables1,);
                setIsReady(true);
                setListTables(listTables1);
                setText("fff-ll");
                console.log("isReady2---", isReady, listTables1, listTables, state);
                state.isReadyCounter = state.isReadyCounter
                    ? state.isReadyCounter + 1
                    : 1;
                forceUpdate();
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state]);



    return (
        <>
            <div>
                <h3 className="text-center">שולחנות</h3>
                <Link to={"/tables/add"} className="btn btn-primary">
                    <span className="px-2">
                        <i className="bi-plus-circle-fill"></i>
                    </span>
                    <span>הוספת שולחן</span>
                </Link>
                <table className="table table-dark">
                    <thead>
                        <tr>
                            <th>שם שולחן</th>
                            <th>סטטוס</th>
                            <th>קיבולת</th>
                            <th className="text-center">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {isReady &&
                            listTables.map((item, i) => {
                                return (
                                    <tr key={i} className="align-items-center">
                                        <td>{item.name}</td>
                                        <td>{item.GroupSeqNum || "פנוי"}</td>
                                        <td>{item.capacity}</td>
                                        <td>
                                            <div className={"text-start"}>
                                                <Link
                                                    to={"/tables/" + item.name}
                                                    className="px-1"
                                                    title="view">
                                                    <Button variant="secondary">
                                                        <i className="bi-eye-fill"></i>
                                                    </Button>
                                                </Link>

                                                {/* <button className={styles.primaryBtn} onClick={() => setIsOpen(true)}>
                                                <i className="bi-trash3-fill"></i>
                                                </button> */}
                                                {/* {isOpen && <Modal setIsOpen={setIsOpen} />} deleteItem(item.name, i)*/}
                                                <Button
                                                    variant="warning"
                                                    onClick={() => Name(item.name)}
                                                    title="delete"
                                                    className={"px-1 mx-2"}>
                                                    <i className="bi-trash3-fill"></i>
                                                </Button>

                                                <Button variant="primary" onClick={handleShow}>
                                                <i className="bi-trash3-fill"></i>
                                                </Button>
                                                <>
                                                <Modal show={show} onHide={handleClose}>
                                                    <Modal.Header closeButton>
                                                        <Modal.Title>מחיקת שולחן</Modal.Title>
                                                    </Modal.Header>
                                                    <Modal.Body> {item.name} בטוח שאתה רוצה למחוק את </Modal.Body>
                                                    <Modal.Footer>
                                                        <Button variant="secondary" 
                                                                onClick={handleClose} 
                                                                className="col btn btn-success m-2 p-2 text-whait align-self-end">
                                                            בטל
                                                        </Button>
                                                        <Button variant="primary" 
                                                                // onClick={handleClose} 
                                                                className="col btn btn-danger m-2 p-2 text-whait align-self-end"
                                                                onClick={() =>
                                                                    BaseService.delete("/tables/", {
                                                                        id: item.name,
                                                                    }).then((rp) => {
                                                                        console.log("rp", rp)
                                                                        if (rp.Status) {
                                                                            const listTablesUpdated = listTables.filter(
                                                                                (tables) => tables.name !== item.name
                                                                            );
                                                                            setListTables(listTablesUpdated);
                                                                            console.log("listTablesUpdated", listTablesUpdated);
                                                                        } else {
                                                                            toastr.error(rp.Messages);
                                                                            console.log("Messages: " + rp.Messages);
                                                                            console.log("Exception: " + rp.Exception);
                                                                        }
                                                                        handleClose()
                                                                    })
                                                                }>
                                                            מחק
                                                        </Button>
                                                    </Modal.Footer>
                                                </Modal>
                                                </>
                                            </div>
                                        </td>
                                    </tr>
                                );
                            })}
                        {/* {isOpen && <Modal setIsOpen={setIsOpen} />} */}
                    </tbody>
                </table>
                {/* <button className={styles.primaryBtn} onClick={() => setIsOpen(true)}>
                    Open Modal
                    </button>
                {isOpen && <Modal setIsOpen={setIsOpen} />} */}

            </div>
        </>
    );
};

export default TablesIndex;
