import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Customers } from "../models/customers";
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal, Form } from "react-bootstrap";
import { Tables } from "../../ ../models/table";



const CustomersIndex = (props) => {
    const { state, dispatch } = useAppState(0);

    const [, updateState] = useState(1);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listCustomers, setListCustomers] = useState([], Array(Customers));
    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);
    const [updateCounter, setUpdateCounter] = useState(0);
    const [table, setTables] = useState({}, Tables);

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [responsServer, setResponsServer] = useState("")


    var pathname = window.location.pathname
    console.log("pathname", pathname)

    const responsServerFun = () => {
        if (responsServer == "No place to sit for any group") {
            return (
                " לא נמצא שולחן פנוי"
            )
        }
        else {
            return (
                responsServer
            )
        }
    }

    const TableWhit = (id) => {
        BaseService.fetch(`/tables/${id}`).then(
            (response) => {
                setIsLoaded(true);
                setTables(response.data, Tables);
                console.log("response.data", response.data);
                console.log("table Daqta", table);
                console.groupEnd();
            },
            (error) => {
                setIsLoaded(true);
                setError(error);
            }
        );
        return (
            <>
                <Table className="table table-dark">
                    <tbody>
                        <tr>
                            <td>id</td>
                            <td>{table.id}</td>
                        </tr>
                        <tr>
                            <td>שם שולחן</td>
                            <td>{table.name}</td>
                        </tr>
                        <tr>
                            <td>סטטוס</td>
                            <td>{table.GroupSeqNum || "פנוי"}</td>
                        </tr>
                        <tr>
                            <td>קיבולת</td>
                            <td>{table.capacity}</td>
                        </tr>
                    </tbody>
                </Table>
            </>
        );
    }

    const Status = (queue) => {
        console.log("GroupSeqNo", queue)
        if (queue == "tobesitted") {
            return (
                "ממתין לשולחן"
            )
        }
        if (queue == "sitting") {
            return (
                "ממתין לשירות"
            )
        }
        if (queue == "awaitingbill") {
            return (
                "ממתין לחשבון"
