import * as React from "react";

import { Button, Table } from "react-bootstrap";
import { useAppState } from "../../../service/AppStateContext.tsx";
import BaseService from "../../../service/base.service.tsx";
import { Customers } from "../models/customers";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { HiArrowNarrowLeft } from "react-icons/hi";

const Price = (priceAll) => {
    if (priceAll.priceAll) {
        return (
            <div>
                {priceAll.priceAll + "₪"}
            </div>
        )
    }
    else {
        return (
            "------"
        )
    }
}

const CustomersView = (props) => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [customer, setCustomers] = useState({}, Customers);


    const path = window.location.pathname;
    console.log("path", path)
    const splited = path.split("/");
    const name = splited[splited.length - 1]
    const category = splited[splited.length - 2]

    const { state, dispatch } = useAppState();

    const mapResevation = (reservation) => {

    }

    useEffect(() => {
        console.group("ssss", state);
        BaseService.fetch(`${path}`).then(
            (response) => {
                setIsLoaded(true);
                setCustomers(response.data, Customers);
                console.log("response.data", response.data);
                console.log("customer Daqta", customer);
                console.groupEnd();
            },
            (error) => {
                setIsLoaded(true);
                setError(error);
            }
        );
    }, [state]);
    return (
        <>      {
            <Table className="table table-dark">
                <Link
                    to={"/customers/"}
                    className="px-1"
                    title="view">
                    <Button variant="secondary">
                        <HiArrowNarrowLeft />
                    </Button>
                </Link>
                <tbody>
                    <tr>
                        <td>שם קבוצה:</td>
                        <td>{customer.name}</td>
                    </tr>
                    <tr>
                        <td>גודל קבוצה:</td>
                        <td>{customer.size }</td>
                    </tr>
                    <tr>
                        <td>שעת הגעה:</td>
                        <td>{customer.GroupSeqNo}</td>
                    </tr>
                    <tr>
                        <td>שולחן:</td>
                        <td>{customer.table || "פנוי"}</td>
                    </tr>
                    <tr>
                        <td>סטטוס:</td>
                        <td>{customer.queue}</td>
                    </tr>
                    <tr>
                        <td>סך לתשלום</td>
                        <td><Price priceAll={customer.priceAll} /></td>
                    </tr>
                    <tr>
                        <td>הזמנות:</td>
                        <td>{customer.reservation}</td>
                    </tr>
                </tbody>
            </Table>
        }
        </>
    );
};
export default CustomersView;
