import * as React from "react";
import "../../../App.css";
import { Link } from "react-router-dom";
import { Customers } from "../models/customers";
import BaseService from "../../../service/base.service.tsx";
import { confirmAlert } from "react-confirm-alert";
import Button from "react-bootstrap/Button";
import { useAppState } from "../../../service/AppStateContext.tsx";
import { useEffect, useReducer, useState } from "react"; // Import css
import * as toastr from "toastr";
import { Modal, Form } from "react-bootstrap";


const IfButton = (props) => {
    const [isReadyRes, setIsReadyRes] = useState(0);

    console.log("aaaaaaaaaaaaa", props.queue, props.GroupSeqNo)
    if (props.queue == "tobesitted") {
        return (
            <Link
                to={"/customers/"}
                className="px-1"
                title="view">
                <Button
                    variant="warning"
                    onClick={() =>
                        BaseService.put('/customers/tobesitted/').then((rp) => {
                            if (rp.Status) {
                                toastr.success("Finance updated.");
                            } else {
                                toastr.error(rp.Messages);
                                console.log("Messages: " + rp.Messages);
                                console.log("Exception: " + rp.Exception);
                            }
                        })
                    }
                    title="delete"
                    className={"px-1 mx-2"}>מצא שולחן
                </Button>
            </Link>

        )
    }
    if (props.queue == "sitting") {
        return (
            <div>
                <Link
                    to={"/customers/sitting/" + props.GroupSeqNo + "/reservation"}
                    className="px-1"
                    title="view">
                    <Button variant="secondary">
                        הזמנות
                    </Button>
                </Link>
                <Link
                    to={"/customers/"}
                    className="px-1"
                    title="view">
                    <Button
                        variant="warning"
                        onClick={() =>
                            BaseService.put('/customers/sitting/' + props.GroupSeqNo).then((rp) => {
                                if (rp.Status) {
                                    toastr.success("Finance updated.");
                                    setIsReadyRes(isReadyRes + 1);
                                } else {
                                    toastr.error(rp.Messages);
                                    console.log("Messages: " + rp.Messages);
                                    console.log("Exception: " + rp.Exception);
                                }
                            })
                        }
                        title="delete"
                        className={"px-1 mx-2"}>גש לתשלום
                    </Button>
                </Link>
            </div>
        )
    }
    if (props.queue == "awaitingbill") {
        return (
            <Button
                variant="warning"
                onClick={() =>
                    BaseService.put('/customers/awaitingBill/' + props.GroupSeqNo).then((rp) => {
                        if (rp.Status) {
                            toastr.success("Finance updated.");
                        } else {
                            toastr.error(rp.Messages);
                            console.log("Messages: " + rp.Messages);
                            console.log("Exception: " + rp.Exception);
                        }
                    })
                }
                title="delete"
                className={"px-1 mx-2"}>סיום
            </Button>
        )
    }
}

const CustomersIndex = (props) => {
    const { state, dispatch } = useAppState(0);

    const [, updateState] = useState(1);
    const [isReady, setIsReady] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [text, setText] = useState("test");
    const [listCustomers, setListCustomers] = useState([], Array(Customers));
    const [ignored, forceUpdate] = useReducer((x) => x + 1, 0);
    const [updateCounter, setUpdateCounter] = useState(0);

    // const [pathname, setpathname] = useState
    var pathname = window.location.pathname
    console.log("pathname", pathname)

    useEffect(() => {
        BaseService.getAll(pathname).then((rp) => {
            if (rp.Status) {
                const data = rp.Data;
                const listCustomers1 = data;
                console.log("isReady1", listCustomers1,);
                setIsReady(true);
                setListCustomers(listCustomers1);
                setText("fff-ll");
                console.log("isReady2---", isReady, listCustomers1, listCustomers, state);
            } else {
                setIsReady(true);
                setHasError(true);
                console.log("Messages: " + rp.Messages);
                console.log("Exception: " + rp.Exception);
            }
        });
    }, [state, pathname]);


    return (
        <>
            <Modal style={{ direction: "rtl" }} show={true} size="lg" >
                <Modal.Body >
                    <div>
                        <h3 className="text-center">קבוצות</h3>
                        <Link to={"/customers/tobesitted/add"} className="btn btn-primary">
                            <span className="px-2">
                                <i className="bi-plus-circle-fill"></i>
                            </span>
                            <span>קבוצה חדשה</span>
                        </Link>
                        <menu className="customer customer-striped" style={{ marginTop: 20 }}>
                            <thead>
                                <tr>
                                    <th>שם קבוצה</th>
                                    <th>גודל קבוצה</th>
                                    <th>שעת הגעה</th>
                                    <th>שולחן</th>
                                    <th>סטטוס</th>
                                    <th className="text-center">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {isReady &&
                                    listCustomers.map((item, i) => {
                                        return (
                                            <tr key={i} className="align-items-center">
                                                <td>{item.name}</td>
                                                <td>{item.size}</td>
                                                <td>{item.GroupSeqNo}</td>
                                                <td>{item.table || "פנוי"}</td>
                                                <td>{item.queue}</td>
                                                <td>
                                                    <div className={"text-start"}>
                                                        <Link
                                                            to={"/customers/" + item.queue + "/" + item.name}
                                                            className="px-1"
                                                            title="view">
                                                            <Button variant="secondary">
                                                                <i className="bi-eye-fill"></i>
                                                            </Button>
                                                        </Link>
                                                    </div>
                                                    <div className="col col-3">
                                                        <IfButton queue={item.queue} GroupSeqNo={item.GroupSeqNo} />
                                                    </div>
                                                </td>
                                            </tr>
                                        );
                                    })}
                            </tbody>
                        </menu>
                    </div>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default CustomersIndex;
