import { useNavigate } from "react-router-dom";
import { Button } from "react-bootstrap";
import { HiArrowNarrowLeft } from "react-icons/hi";
import BaseService from "../service/base.service.tsx";
import * as toastr from "toastr";
import MenusView from "../modules/menu/components/view.component";
import CustomersView from "../modules/diners/components/view.component";

let props1

const openModal = (props) => {
    let modal = document.getElementById("myModal").style.display = "block";
    console.log("props1", props)
    props1 = props
    console.log()
}

const closeModal = (props) => {
    let modal = document.getElementById("myModal").style.display = "none"
}

export const Modal = (props) => {
    // setNameForDelete(name)
    // setCategoryForDelete(category)
    // setIdForDelete(id)
    // console.log("name, category, id", name, category, id)
    // handleShow()
    console.log("props1", props)
    props1 = props

    // Get the modal
    var modal = document.getElementById("myModal").style.display = "block";

    // Get the button that opens the modal
    var btn = document.getElementById("myBtn");

    // Get the <span> element that closes the modal
    var span = document.getElementsByClassName("close")[0];

    // When the user clicks the button, open the modal 
    // btn.onclick = function () {
    //     modal.style.display = "block";
    // }

    // When the user clicks on <span> (x), close the modal
    span.onclick = function () {
        modal.style.display = "none";
    }

    // When the user clicks anywhere outside of the modal, close it
    window.onclick = function (event) {
        if (event.target == modal) {
            modal.style.display = "none";
        }
    }
}

export const GoBack = () => {
    let navigate = useNavigate();
    return (
        <Button className="px-1"
            onClick={() => navigate(-1)}
            variant="warning"
            title="Back">
            <HiArrowNarrowLeft /> חזור
        </Button>
    )
}

export const View = (props) => {
    console.log("props", props)
    return (
        <>
            <button id="myBtn" onClick={() => openModal({...props})}>
                <img src="https://img.icons8.com/ios-glyphs/30/ab5e2a/visible--v1.png" />
            </button>

            <div id="myModal" class="modal">

                <div class="modal-content">
                    <div>
                        <div id="Xclose" class="close" >
                            <img src="https://img.icons8.com/ios-glyphs/30/000000/delete-sign.png" />
                        </div>
                    </div>
                    <div>
                        <CustomersView {...props} />
                    </div>
                    <div>
                        <button class="close" onClick={() => closeModal()}>
                            אישור
                        </button>
                    </div>
                </div>
            </div>
        </>
    )
}

export const Reservation = (props) => {
    return (
        <Button class="iconButton" onClick={() => { window.location.href = `/diners/sit/${props.id}/reservation` }}>
            <img src="https://img.icons8.com/ios-filled/30/ab5e2a/meal.png" />
        </Button>
    )
}

export const Payment = (props) => {
    return (
        <Button class="iconButton" onClick={() =>
            BaseService.put(`/diners/sit/${props.id}`).then((rp) => {
                if (rp.Status) {
                    toastr.success("Finance updated", rp.Data);
                } else {
                    toastr.error(rp.Messages);
                    console.log("Messages: " + rp.Messages);
                    console.log("Exception: " + rp.Exception);
                }
                // window.location.reload()
            })}>
            <img src="https://img.icons8.com/external-kmg-design-glyph-kmg-design/30/ab5e2a/external-payment-payment-kmg-design-glyph-kmg-design-1.png" />
        </Button>
    )
}

export const Completion = (props) => {
    return (
        <Button class="iconButton" onClick={() =>
            BaseService.put('/diners/awaitingBill/' + props.GroupSeqNo).then((rp) => {
                if (rp.Status) {
                    toastr.success("Finance updated", rp.Data);
                } else {
                    toastr.error(rp.Messages);
                    console.log("Messages: " + rp.Messages);
                    console.log("Exception: " + rp.Exception);
                }
                window.location.reload()
            })
        }>
            <img src="https://img.icons8.com/ios-filled/30/ab5e2a/checked-checkbox.png" />
        </Button>
    )
}
